import React, { Component } from 'react';
import { ModalOpener } from './modalOpener';
import { TextInput } from '../textInput';
import image from './Images/DeliverTask.svg';
import Axios from 'axios';

export class DeliverTask extends Component {
    myForm = () => {
        return (
            <form>{/*Devuelve un form con toda la información necesaria para ingresar un pago (la fecha se ingresa sola)*/}
                <TextInput name={'WorkshopName'} label={'Nombre del/la tallerista'} />
                <TextInput name={'Number'} label={'Número de tarea'} />
                {/*Debería existir un botón que deje ingresar más items*/}
                <TextInput name={'ArticleDelivered'} label={'Artículo entregado'} />
                <TextInput name={'QuantityDelivered'} label={'Cantidad entregada'} />
            </form>
        );
    }

    render() {
        return (
            <ModalOpener buttonText='Recibir tarea' children={this.myForm()}
                className={'title'} logo={image} title={'Ingreso de tarea'} post={() => {
                    Axios.post('http://localhost:3001/create', {});
                }} />
            //Crea un botón que abre a un modal en el que aparecerá lo devuelto en this.myForm
        );
    }
}
