import React from 'react';
import { TextInput } from '../../textInput';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button'
import PropTypes from 'prop-types';
import Axios from 'axios';

export const Article = ({ registration }) => {

    if (registration) {//de ser true, se está dando el alta, de lo contrario, la baja
        let id = 0, price = 0, description = 0;
        return (
            <Form>
                <Form.Group className="mb-3">
                    <Form.Label>Número de Artículo</Form.Label>
                    <Form.Control type="text" placeholder="Ingrese el número único…" />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Label>Precio por unidad</Form.Label>
                    <Form.Control type="text" placeholder="Ingrese el precio…" />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Label>Descripción</Form.Label>
                    <Form.Control type="textarea" rows={2} placeholder="Ingrese una descripción del artículo…" />
                </Form.Group>

                <Button variant="primary" type="submit" onClick={function () {
                    Axios.post('http://localhost:3001/create', { id, price, description });
                }}>
                    Submit
                </Button>
            </Form>
            // <form>{/*Devuelve el form para dar de alta a un artículo. Pide todos los datos necesarios*/}
            //     <TextInput name={'Number'} label={'Número de Artículo'} onChange={()=>{updateValue()}}/>
            //     <TextInput name={'Price'} label={'Precio por unidad'} onChange={()=>{updateValue()}}/>
            //     <TextInput name={'Description'} label={'Descripción'} onChange={()=>{updateValue()}}/>
            //     <button onClick={function () {
            //         Axios.post('http://localhost:3001/create', {id, price, description});
            //     }}>Aceptar</button>
            // </form>
        );
    }
    else
        return (
            <form>{/*Devuelve el form para dar de baja a un artículo. Pide todos los datos necesarios*/}
                <TextInput name={'Number'} label={'Número de Artículo'} />
                <button onClick={function () {
                    Axios.post('http://localhost:3001/create', {});
                }}>Aceptar</button>
            </form>
        );
}

Article.propTypes = {
    registration: PropTypes.bool.isRequired //True si y sólo si se da un alta
}
