import PropTypes from 'prop-types';

export const TextInput = ({ onChange, name, label }) => {//Pedir치 un dato con un input tipo text
    return (
        <div className={'div' + name}>
            <label>{label + ':  '}</label>
            <input type="text" name="name" autoComplete="off" onChange={onChange} />
        </div>
    );
}

export function updateValue(var0, var1) {
    this.setState({var0: var1});
}

TextInput.propTypes = {
    onChange: PropTypes.func,//Funci칩n a ejecutar cuando se modifica el input
    name: PropTypes.string.isRequired,//Nombre de la clase que va a tener el div
    label: PropTypes.string.isRequired//Mostrado en la label
}
