import React, { Component } from 'react';
import { ModalOpener } from './ModalOpener';

export class NewTaskButton extends ModalController {
    render() {
        return (
            <div>
                <button onClick={this.showModal}>
                    Asignar Tarea
                </button>
                <Modal show={this.state.showModal} handleClose={this.hideModal} />
            </div>
        );
    }
}

export class Modal extends Component {
    MyModal() {
        return (
            <form>
                <TextInput name={'WorkshopName'} label={'Nombre del/la tallerista'} />
                <TextInput name={'Article'} label={'Artículo'} />
                <TextInput name={'Quantity'} label={'Cantidad'} />
                <TextInput name={'Number'} label={'Número de tarea'} />
            </form>
        );
    }

    render() {
        return (
            <ModalPrototype show={this.props.show} handleClose={this.props.handleClose} children={this.MyModal()} />
        );
    }
}
