import React, { Component } from 'react';
import { ModalOpener } from './modalOpener';
import { TextInput } from '../textInput';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import image from './Images/DeliverTask.svg';
import Axios from 'axios';

export class DeliverTask extends Component {
    state = {
        name: '',
        number: '',
        article: '',
        quantity: ''
    }

    changeValue = (state, val) => {
        this.setState({ [state]: val });
    }

    myForm = () => {
        return (
            <Form>
                <Form.Group className="mb-3">
                    <Form.Label>Nombre del taller</Form.Label>
                    <Form.Control type="text" placeholder="Ingrese el nombre del/la tallerista…" onChange={(event) => {
                        this.setState({workshop: event.target.value});
                    }} />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Label>Número de tarea</Form.Label>
                    <Form.Control type="text" placeholder="Ingrese el número de tarea…" onChange={(event) => {
                        this.setState({task: event.target.value});
                    }} />
                </Form.Group>

                {/*Debería existir un botón que deje ingresar más items*/}

                <Form.Group className="mb-3">
                    <Form.Label>Número de artículo entregado</Form.Label>
                    <Form.Control type="text" placeholder="Ingrese el código del artículo…" onChange={(event) => {
                        this.setState({article: event.target.value});
                    }} />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Label>Cantidad entregada</Form.Label>
                    <Form.Control type="text" placeholder="Ingrese la cantidad de artículos…" onChange={(event) => {
                        this.setState({quantity: event.target.value});
                    }} />
                </Form.Group>
            </Form>
        );
    }

    post = () => {
        Axios.post('http://localhost:3001/create',
            { name: this.state.name, number: this.state.number, article: this.state.article, quantity: this.state.quantity });
    }

    render() {
        return (
            <ModalOpener buttonText='Recibir tarea' children={this.myForm()}
                className={'title'} logo={image} title={'Ingreso de tarea'} post={this.post} />
            //Crea un botón que abre a un modal en el que aparecerá lo devuelto en this.myForm
        );
    }
}
