import React, { Component, useState } from 'react';
import { ArtWorkButton } from './RegistrationDrop/registrationDrop';
import { NewPaymentButton } from './newPayment'
import { NewTaskButton } from './newTask';
import { NavegationButton } from './navegation';
import { DeliverTask } from './deliverTask';
import { ModifyDataButton } from './modifyData';
import './styles.css';

import { Dropdown as BootstrapDropdown } from 'react-bootstrap';

export default class Menu extends Component {
  render() {
    return (
      <>
        <ul className='menu-selector'>
          <li><NewPaymentButton /></li>
          <li><NewTaskButton /></li>
          <li><NavegationButton /></li>
          <li><DeliverTask /></li>
          <li><ArtWorkButton article={false} /></li>
          <li><ArtWorkButton article={true} /></li>
          <li><ModifyDataButton /></li>
        </ul>
        <Parent />
      </>
    );
  }
}



const Dropdown = (props) => {
    return (
        <BootstrapDropdown>
            <BootstrapDropdown.Toggle variant='success' id='dropdown'>
                <span>Selection</span>
            </BootstrapDropdown.Toggle>

            <BootstrapDropdown.Menu>
                {props.items.map((name, index) => (
                    <BootstrapDropdown.Item
                        key={index}
                        onClick={(event) => {
                            console.log(event.target.text);
                            props.setSelected(event.target.text);
                        }}
                        value={name}
                    >
                        {name}
                    </BootstrapDropdown.Item>
                ))}
            </BootstrapDropdown.Menu>
        </BootstrapDropdown>
    );
};

function Parent() {
    const [typeFilter, setTypeFilter] = useState('');

    const allItems = [
        { name: 'Coffee Milk Tea', type: 'Tea' },
        { name: 'Earl Gray Milk Tea', type: 'Tea' },
        { name: 'Orange Juice', type: 'Juice' },
        { name: 'Wheatgrass Juice', type: 'Juice' },
    ];

    const itemsToShow = allItems
        .filter((item) => {
            if (typeFilter) {
                return item.type === typeFilter;
            }
            return true;
        })
        .map((item, i) => {
            return <li key={i}>{item.name}</li>;
        });

    return (
        <div>
            <Dropdown items={['Tea', 'Juice']} setSelected={setTypeFilter} />
            <ol>{itemsToShow}</ol>
        </div>
    );
}
