import React, { Component } from 'react';
import { ModalOpener } from './modalOpener';
import { Request, TaskRequest } from '../textInputs';
import image from './Images/AsignTask.svg';
import Axios from 'axios';
import Form from 'react-bootstrap/Form';
import moment from 'moment';

export class AsignTaskButton extends Component {
    state = {
        name: '',
        task: '',
        deadline: '',
        weight: '',
        threads: '',
        //Auto-asignar
        date: moment(new Date()).format("DD/MM/YYYY"),
        packages: '',
        description: '',
        quantity: '',
        //Otros
        selectedTask: [],
        tasks: [],
        title: 'Elegir Tarea'
    }

    componentDidUpdate(prevProps, prevState) {
        if (this.state.selectedTask === prevState.selectedTask)
          return;
        this.setState({ packages: selectedTask.packages, description: selectedTask.description,
            quantity: selectedTask.quantity });

      }

    constructor(props) {
        super(props);
        Axios.post('http://localhost:3001/getTasks', { state: 'toAsign' }).then((response) => {
            this.setState({ tasks: response.data });
        });
    }

    myForm = () => {
        return (
            <>
                <Request toShow="name" onChange={(event) => {
                    this.setState({ name: event });
                }} />

                <Request toShow="deadline" onChange={(event) => {
                    this.setState({ deadline: event.target.value });
                }} />

                <TaskRequest setSelectedTask={(e) => this.setState({ selectedTask: e })} tasks={this.state.tasks}
                    title={this.state.title} setTitle={(e) => { this.setState({ title: e }) }} />

                <Request toShow="weight" onChange={(event) => {
                    this.setState({ weight: event.target.value });
                }} />

                <Request toShow="price" onChange={(event) => {
                    this.setState({ price: event.target.value });
                }} />

                <Request toShow="threads" onChange={(event) => {
                    this.setState({ threads: event.target.value });
                }} />
            </>
        );
    }

    post = () => {
        let aux = this.state;
        Axios.put('http://localhost:3001/asignTask', { 
            name: aux.name, task: aux.task, deadline: aux.deadline, weight: aux.weight, threads: aux.threads,
            date: aux.date,
        })
    }

    render() {
        return (
            <ModalOpener buttonText='Taller' children={this.myForm()}
                className={'title'} logo={image} title={'Asignar Tarea'} post={this.post} />
            //Crea un bot칩n que abre a un modal en el que aparecer치 lo devuelto en this.myForm
        );
    }
}
