import React, { useState, useEffect, Component, Fragment } from 'react';
import PropTypes from 'prop-types';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import Axios from 'axios';
import './autocomplete.css';

export const Request = ({ onChange, toShow, label, reg }) => {//Pedirá un dato con un input tipo text

    let placeholder, auxStorage = label;

    switch (toShow) {
        case 'name':
            label = 'Nombre del taller';
            placeholder = 'Ingrese el nombre del/la tallerista';
            break;
        case 'article':
            label = 'Número de artículo';
            placeholder = 'Ingrese el código único del artículo';
            break;
        case 'quantityArticle':
            label = 'Cantidad asignada';
            placeholder = 'Ingrese la cantidad de artículos';
            break;
        case 'task':
            label = 'Número de tarea';
            placeholder = 'Ingrese el número de tarea';
            break;
        case 'money':
            label = 'Monto';
            placeholder = 'Ingrese el monto a pagar';
            break;
        case 'price':
            label = 'Precio';
            placeholder = 'Ingrese el precio por unidad';
            break;
        case 'description':
            label = 'Descripción';
            placeholder = 'Ingrese una breve descripción del artículo';
            break;
        default:
            console.log("ERROR, HA INGRESADO EL toShow " + toShow + " EN EL COMPONENTE TEXTINPUT");
    }
    label = (auxStorage) ? auxStorage : label;
    placeholder += '…';
    return ((toShow === 'name' && !reg) ? <NameRequest label={label} onSelect={onChange} /> :
        <Form.Group className="mb-3">
            <Form.Label>{label}</Form.Label>
            <InputGroup>
                {(toShow === 'money') ? <InputGroup.Text>$</InputGroup.Text> : <></>}
                <FormControl type="text" placeholder={placeholder} onChange={onChange} />
                {(toShow === 'money') ? <InputGroup.Text>.00</InputGroup.Text> : <></>}
            </InputGroup>
        </Form.Group>
    );
}
Request.propTypes = {
    onChange: PropTypes.func.isRequired, //Función a ejecutar cuando se modifica el input
    toShow: PropTypes.string.isRequired,
    reg: PropTypes.bool,//True si se está dando de alta
    label: PropTypes.string
}

export const NameRequest = ({ label, onSelect }) => {

    const [list, setList] = useState([]);
    useEffect(() => {
        getList();
        return () => {
            setList([]); // This worked for me
        };
    }, []);
    const getList = () => {
        Axios.get('http://localhost:3001/dropdownNames').then((response) => {
            setList(response.data);
        });
    }
    return (
        <Form.Group className="mb-3">
            <Form.Label>{label}</Form.Label>
            <Autocomplete suggestions={list.map((element) => { return element.name })} onSelect={onSelect}/>
        </Form.Group>
    );
}
NameRequest.propTypes = {
    label: PropTypes.string.isRequired
}


class Autocomplete extends Component {
    static propTypes = {
      suggestions: PropTypes.instanceOf(Array)
    };
  
    static defaultProps = {
      suggestions: []
    };
  
    constructor(props) {
      super(props);
  
      this.state = {
        // The active selection's index
        activeSuggestion: 0,
        // The suggestions that match the user's input
        filteredSuggestions: [],
        // Whether or not the suggestion list is shown
        showSuggestions: false,
        // What the user has entered
        userInput: ""
      };
    }
  
    onChange = e => {
      const { suggestions } = this.props;
      const userInput = e.currentTarget.value;
  
      // Filter our suggestions that don't contain the user's input
      const filteredSuggestions = suggestions.filter(
        suggestion =>
          suggestion.toLowerCase().indexOf(userInput.toLowerCase()) > -1
      );
  
      this.setState({
        activeSuggestion: 0,
        filteredSuggestions,
        showSuggestions: true,
        userInput: e.currentTarget.value
      });
    };
  
    onClick = e => {
      this.setState({
        activeSuggestion: 0,
        filteredSuggestions: [],
        showSuggestions: false,
        userInput: e.currentTarget.innerText
      });
    };
  
    onKeyDown = e => {
      const { activeSuggestion, filteredSuggestions } = this.state;
  
      // User pressed the enter key
      if (e.keyCode === 13) {
        this.setState({
          activeSuggestion: 0,
          showSuggestions: false,
          userInput: filteredSuggestions[activeSuggestion]
        });
      }
      // User pressed the up arrow
      else if (e.keyCode === 38) {
        if (activeSuggestion === 0) {
          return;
        }
  
        this.setState({ activeSuggestion: activeSuggestion - 1 });
      }
      // User pressed the down arrow
      else if (e.keyCode === 40) {
        if (activeSuggestion - 1 === filteredSuggestions.length) {
          return;
        }
  
        this.setState({ activeSuggestion: activeSuggestion + 1 });
      }
    };
  
    render() {
      const {
        onChange,
        onClick,
        onKeyDown,
        state: {
          activeSuggestion,
          filteredSuggestions,
          showSuggestions,
          userInput
        }
      } = this;
  
      let suggestionsListComponent;
  
      if (showSuggestions && userInput) {
        if (filteredSuggestions.length) {
          suggestionsListComponent = (
            <ul className="suggestions">
              {filteredSuggestions.map((suggestion, index) => {
                let className;
  
                // Flag the active suggestion with a class
                if (index === activeSuggestion) {
                  className = "suggestion-active";
                }
  
                return (
                  <li className={className} key={suggestion} onClick={onClick}>
                    {suggestion}
                  </li>
                );
              })}
            </ul>
          );
        } else {
          suggestionsListComponent = (
            <div className="no-suggestions">
              <em>No suggestions, you're on your own!</em>
            </div>
          );
        }
      }
  
      return (
        <Fragment>
          <input
            type="text"
            onChange={onChange}
            onKeyDown={onKeyDown}
            value={userInput}
          />
          {suggestionsListComponent}
        </Fragment>
      );
    }
  }
