import React, { Component } from 'react';
import { ModalOpener } from '../modalOpener';
import { Request, TaskRequest } from '../textInputs';
import Axios from 'axios';
import Form from 'react-bootstrap/Form';
//import image from './Images/newQuery.svg';

const initialState = {
    customer : " ", //A침ade cliente  
    observation : " ",
}

export class NewQuery extends Component {
    state = initialState;

    resetState = () => {
        this.setState(initialState);
    }

    myForm = () => {
        return(
            <>
                <Request toShow="customerName" onChange={(e) => {
                    this.setState({customer: e.target.value});
                }}/>
                <Request toShow="observation" onChange={(e) => {
                    this.setState({observation: e.target.value});
                }}/>
            </>
        );
    }

    // post = () => {
    //     return(

    //     );
    // }

    myForm = () => {
        return (
            <Table striped bordered id="taskTable">
            <thead>
                <tr>
                    <th>ID</th>
                    <th>Nombre</th>
                    <th>Descripci칩n</th>
                    <th>Cantidad</th>
                    <th>Peso</th>
                    <th>Metros</th>
                    <th></th>{/* Bot칩n de eliminar fila */}
                </tr>
            </thead>
            <tbody>
                {!this.state.materials? null :
                this.state.materials.map((material, index) =>
                <Row material={material} index={index} remove={i => this.removeMaterial(i)} materialsData={this.state.materialsData}
                    onChange={(e, property) => this.changeMaterial(index, e, property)} />
                )}
                <Row material={{id: '', quantity: ''}} onChange={(value, property) => {
                    console.log('hi');
                    this.addMaterial(value, property);
                }} materialsData={this.state.materialsData} />
            </tbody>
        </Table>
        );
    }

    render() {
        return (
            <ModalOpener buttonText='Nuevo pedido' handleClose={this.resetState}
                className={'title'} /*logo={image}*/ title={'Nuevo pedido'} post={this.post} children={this.myForm()} />
            
        );
    }
}
