import React, {Component} from 'react';
import ReactDOM from 'react-dom';

export class Parent extends Component{

    state = {
        show: false,
        drop: false,
        registration: false
    }

    
    changeStates = reg =>{
        this.setState({show: !this.state.show, registration: reg, drop: !reg});
        console.debug("HI");
    }
    
    render(){
        return(
            <div>
                <button>Talleres</button>
                <button>Art√≠culo</button>
                <RegistrationDropButton display = {this.changeStates} />
            </div>
        );
    }
}


export class RegistrationDropButton extends Component{
    onClick = event => {
        this.props.changeValue(event.target.value)
    }

    render(){

        return(
            
            <div>
                <button onClick={this.onClick} value={true}>
                    Altas
                </button>
                <button onClick={this.onClick} value={false}>
                    Bajas
                </button>
            </div>
        );
    }
}
