import React, { Component } from 'react';
import { ModalOpener } from './modalOpener';
import { Request } from '../textInputs';
import image from './Images/NewPayment.svg';
import Axios from 'axios';
import Form from 'react-bootstrap/Form';

export class NewPaymentButton extends Component {
    state = {
        name: '',
        quantity: ''
    }

    ex = (element) => {
        this.setState({ name: element });
    }
    myForm = () => {
        return (
            <Form>
                <label>Ingrese el número de tarea</label>
                <input onChange={(event) => {
                    this.setState({ quantity: event.target.value });
                }} />
            </Form>
        );
    }

    post = () => {
        Axios.delete(`http://localhost:3001/delete/${quantity}`).then((response) => {
            console.log(response);
        });
    }

    render() {
        return (
            <ModalOpener buttonText='Ingresar pago' children={this.myForm()}
                className={'title'} logo={image} title={'Ingreso de pago'} post={this.post} />
            //Crea un botón que abre a un modal en el que aparecerá lo devuelto en this.myForm
        );
    }
}
