import React, { Component } from 'react';
import { ModalOpener } from './modalOpener';
import { Request, TaskRequest } from '../textInputs';
import image from './Images/AsignTask.svg';
import Axios from 'axios';
import Form from 'react-bootstrap/Form';
import moment from 'moment';

export class AsignTaskButton extends Component {
    state = {
        name: '',
        task: '',
        deadline: '',
        weight: '',
        threads: '',
        //Auto-asignar
        date: '',
        packages: '',
        description: '',
        quantity: '',
        //Otros
        selectedTask: [],
        tasks: [],
        title: 'Elegir Tarea'
    }


    myForm = () => {
        //TODO: Buscar las tareas deseadas
        return (
            <>
                <Request toShow="name" onChange={(event) => {
                    this.setState({ name: event.target.value });
                }} />

                <TaskRequest setSelectedTask={(e) => this.setState({ selectedTask: e })} tasks={this.state.tasks}
                    title={this.state.title} setTitle={(e) => {this.setState({ title: e })}} />
            </>
        );
    }

    post = () => {

    }

    render() {
        return (
            <ModalOpener buttonText='Taller' children={this.myForm()}
                className={'title'} logo={image} title={'Asignar Tarea'} post={this.post} />
            //Crea un bot칩n que abre a un modal en el que aparecer치 lo devuelto en this.myForm
        );
    }
}
