import React, { Component } from 'react';
import Axios from 'axios';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Table from 'react-bootstrap/Table';

const initialState = {
    data: '',
    table: '',
    titles: []
};

export class ShowData extends Component {

    state = initialState;

    resetState = () => {
        this.setState(initialState);
    }

    setData = e => {
        Axios.get('http://localhost:3307/get' + e.charAt(0).toUpperCase() + e.slice(1)).then(response => {
            const res = response.data;
            const table = [];
            console.log(res);
            if (res)
                res.map(row => table.push(Object.values(row)));
            this.setState({ titles: Object.getOwnPropertyNames(res[0]), table, data: e });
        });
    }

    header = (name, index) => {
        let header = name, show = true;
        switch (name) {
            case 'id':
                header = 'Código';
                show = (this.state.data !== 'workshop' && this.state.data !== 'payments');
                break;
            case 'description':
                header = 'Descripción';
                break;
            case 'name':
                header = 'Nombre';
                break;
            case 'contact':
                header = 'Contacto';
                break;
            case 'money':
                header = (this.state.data === 'Workshop') ? 'Cuenta corriente' : 'Dinero';
                break;
            case 'article_id':
                header = 'Código de artículo';
                break;
            case 'article_description':
                header = 'Descripción de artículo';
                break;
            case 'quantity':
                header = 'Cantidad';
                break;
            case 'packages':
                header = 'Bultos';
                break;
            case 'cutDate':
                header = 'Fecha de corte';
                break;
            case 'fabrics':
                header = 'Telas';
                break;
            case 'colors':
                header = 'Colores';
                break;
            case 'responsable':
                header = 'Responsable/s';
                break;
            case 'generalFeatures':
                header = 'Detalles';
                break;
            case 'state':
                header = 'Estado';
                break;
            case 'exitDate':
                header = 'Fecha de salida';
                break;
            case 'deadline':
                header = 'Fecha esperada';
                break;
            case 'weight':
                header = 'Peso';
                break;
            case 'price':
                header = 'Precio unitario';
                break;
            case 'threads':
                header = 'Hilos entregados';
                break;
            case 'calification':
                header = 'Calificación';
                break;
            case 'observations':
                header = 'Observaciones';
                break;
            case 'faulty':
                header = 'Fallados';
                break;
        }
        if(show) this.setState(index === 0 ? { titles: [...this.state.titles, name] } : { titles: [name] });
        return (show ? <th key={index}>{header}</th> : null);
    }

    table = (row, index) => {
        const { titles, table, data } = this.state;
        return <tr key={index}>{row.map((cell, i) => {
            console.log(row);
            console.log(titles.indexOf(row[i]));

            return (cell || cell === 0 || titles.indexOf(row[i])) ? <td key={i}>{cell}</td> : null;
        })}</tr>
    }

    render() {
        const { table, titles } = this.state, data = { 'articles': 'Artículos', 'workshops': 'Talleres', 'payments': 'Pagos', 'tasks': 'Tareas' };
        let title = 'Elegir datos a mostrar', dropdownList = [];
        for (const key in data)
            dropdownList.push(key);
        return (
            <>
                <DropdownButton title={title} onSelect={e => {
                    this.setData(e);
                    title = data.e;
                }}>
                    {dropdownList.map((e, index) => <Dropdown.Item key={index} eventKey={e}>{data[e]}</Dropdown.Item>)}
                </DropdownButton>
                {(titles && table) ?
                    <Table striped bordered>
                        <thead>
                            <tr>
                                {titles.map((title, i) => this.header(title, i))}
                            </tr>
                        </thead>
                        <tbody>
                            {table.map((row, i) => this.table(row, i))}
                        </tbody>
                    </Table>
                    : null}
            </>
        );
    }
}
