import React, { Component } from "react";
import Axios from "axios";

const initialState = {
    password: "",
    actualPassword: "",
    user: "",
    error: false
};

export default class Login extends Component {
    state = initialState;

    componentDidUpdate(prevProps) {
        if (prevProps.user !== this.state.user) {
            console.log(prevProps.user + "      " + this.state.user)
        }
        // this.setState({ actualPassword: 123 });
    };

    submit = () => {
        console.log('submit');
        //window.open(this.state.user,'_self');
        // TODO: Debe existir una págnia para cada usuario que se llame vendor, production, workshops, expedition o manager
    };

    render() {
        return (
            <>
                <form>
                    <select onChange={(user) => {
                        console.log(user.target.value)
                        Axios.post('http://localhost:3001/getPassword', { user: user.target.value }).then((response) => {
                            console.log(response.data[0].password);
                            this.setState({ actualPassword: response.data, user: user.target.value });// TODO: Verificar que lo devuelto esté bien
                        });
                    }}>
                        <option value="vendor">Vendedor</option>
                        <option value="production" selected>
                            Producción
                        </option>
                        <option value="workshops">Administración de talleres</option>
                        <option value="expedition">Expedición</option>
                        <option value="manager">Gerencia</option>
                    </select>
                    <input type="password" onChange={(e) => this.setState({ password: e.target.value })}></input>
                    {this.state.error ? <p>ERROR</p> : null}
                    <button onClick={
                        (e) => {
                            e.preventDefault();
                            const error = !this.state.user && (this.state.password !== this.state.actualPassword);
                            this.setState({ error });
                            console.log(this.state.actualPassword);
                            console.log(this.state.password);
                            if (!error)
                                this.submit();
                        }
                    }>
                        Enviar
                    </button>
                </form>
            </>
        );
    }
}
