import React, { useState, useEffect, useRef } from 'react';
import PropTypes from 'prop-types';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import Dropdown from 'react-bootstrap/Dropdown';
import Overlay from 'react-bootstrap/Overlay';
import Button from 'react-bootstrap/Button';
import Axios from 'axios';

export const Request = ({ onChange, toShow, label, reg }) => {//Pedirá un dato con un input tipo text

    let placeholder, auxStorage = label;

    switch (toShow) {
        case 'name':
            label = 'Nombre del taller';
            placeholder = 'Ingrese el nombre del/la tallerista';
            break;
        case 'article':
            label = 'Número de artículo';
            placeholder = 'Ingrese el código único del artículo';
            break;
        case 'quantityArticle':
            label = 'Cantidad asignada';
            placeholder = 'Ingrese la cantidad de artículos';
            break;
        case 'task':
            label = 'Número de tarea';
            placeholder = 'Ingrese el número de tarea';
            break;
        case 'money':
            label = 'Monto';
            placeholder = 'Ingrese el monto a pagar';
            break;
        case 'price':
            label = 'Precio';
            placeholder = 'Ingrese el precio por unidad';
            break;
        case 'description':
            label = 'Descripción';
            placeholder = 'Ingrese una breve descripción del artículo';
            break;
        default:
            console.log("ERROR, HA INGRESADO EL toShow " + toShow + " EN EL COMPONENTE TEXTINPUT");
    }
    label = (auxStorage) ? auxStorage : label;
    placeholder += '…';
    return ((toShow === 'name' && !reg) ? <NameRequest label={label} placeholder={placeholder} /> :
        <Form.Group className="mb-3">
            <Form.Label>{label}</Form.Label>
            <InputGroup>
                {(toShow === 'money') ? <InputGroup.Text>$</InputGroup.Text> : <></>}
                <FormControl type="text" placeholder={placeholder} onChange={onChange} />
                {(toShow === 'money') ? <InputGroup.Text>.00</InputGroup.Text> : <></>}
            </InputGroup>
        </Form.Group>
    );
}
Request.propTypes = {
    onChange: PropTypes.func.isRequired, //Función a ejecutar cuando se modifica el input
    toShow: PropTypes.string.isRequired,
    reg: PropTypes.bool,//True si se está dando de alta
    label: PropTypes.string
}

export const NameRequest = ({ label, placeholder }) => {


    return (
        <Form.Group className="mb-3">
            <Form.Label>{label}</Form.Label>
            <InputGroup>
                <DropdownWithFilter />
            </InputGroup>
        </Form.Group>
    );
}
NameRequest.propTypes = {
    label: PropTypes.string.isRequired,
    placeholder: PropTypes.string.isRequired
}


const DropdownWithFilter = () => {
    const [list, setList] = useState([]);
    const [show, setShow] = useState(false);
    const target = useRef(null);

    useEffect(() => {
        Axios.get('http://localhost:3001/dropdownNames').then((response) => {
            setList(response.data);
        });
    }, []);

const CustomMenu = React.forwardRef(
        ({ children }, ref) => {
            const [value, setValue] = useState('');
            return (
                <div>
                    <FormControl ref={target}
                        onChange={(e) => {
                            setValue(e.target.value);
                            setShow(true);
                        }}
                    />
                    <ul>
                        {React.Children.toArray(children).filter(
                            (child) =>
                                !value || child.props.children.toLowerCase().startsWith(value.toLowerCase()),
                        )}
                    </ul>
                </div>
            );
        },
    );

    return (
        <>
        <Button ref={target} onClick={() => setShow(true)}>Press ME</Button>
        <Overlay target={target.current} show={show} placement="right">
            {({ placement, arrowProps, show: _show, popper, ...props }) => (
                <div
                    {...props}
                    style={{
                        ...props.style,
                    }}
                >
                    <h1>Hi</h1>
                </div>
            )}
        </Overlay>
        </>
        /*<Dropdown>
                        <Dropdown.Menu as={CustomMenu}>
                            {list.map((element, key) => {
                                return (
                                    <Dropdown.Item key={key}>{element.name}</Dropdown.Item>
                                );
                            })}
                        </Dropdown.Menu>
                    </Dropdown>*/
    );

}
