import React, { Component } from 'react';
import { ModalOpener } from './modalOpener';
import { Request } from '../textInputs';
import image from './Images/NewTask.svg';
import Axios from 'axios';
import Form from 'react-bootstrap/Form';
import moment from 'moment';

export class NewTaskButton extends Component {
    state = {
        name: '',
        article: '',
        quantity: '',
        task: '',
        weight: '',
        price: '',
        deadline: ''
    }


    myForm = () => {
        Axios.get('http://localhost:3001/getTaskCount').then(response => {
            this.setState({ task: response.data[response.data.length - 1] });
        });
        return (
            <Form>
                <Request toShow="name" onChange={(element) => {
                    this.setState({ name: element });
                }} />

                <Request toShow="article" onChange={(event) => {
                    this.setState({ article: event.target.value });
                }} />

                <Request toShow="quantityArticle" onChange={(event) => {
                    this.setState({ quantity: event.target.value });
                }} />

                <Request toShow="task" onChange={(event) => {
                    this.setState({ task: event.target.value });
                }} />

                <Request toShow="weight" onChange={(event) => {
                    this.setState({ weight: event.target.value });
                }} />

                <Request toShow="price" onChange={(event) => {
                    this.setState({ price: event.target.value });
                }} />

                <Request toShow="deadline" onChange={(event) => {
                    this.setState({ deadline: event.target.value });
                }} />
            </Form>
        );
    }

    post = () => {
        
        Axios.post('http://localhost:3001/getDescriptionWhere', { id: this.state.article }).then((response) => {
            console.log(this.state.task);
            console.log(`http://localhost:3001/deleteTask/${this.state.task}`);
            Axios.delete(`http://localhost:3001/deleteTask/${this.state.task}`).then(() => {
                Axios.post('http://localhost:3001/updateTaskCount', { task: this.state.task });
                Axios.post('http://localhost:3001/newTask',
                    {
                        name: this.state.name, article: this.state.article, quantity: this.state.quantity,
                        task: this.state.task, date: moment(new Date()).format("DD/MM/YYYY"), weight: this.state.weight,
                        price: this.state.price, article_description: response.data[0].description, deadline: this.state.deadline
                    });
            });
        });
    }
    render() {
        return (
            <ModalOpener buttonText='Nueva tarea' children={this.myForm()}
                className={'title'} logo={image} title={'Egreso de tarea'} post={this.post} />
            //Crea un bot칩n que abre a un modal en el que aparecer치 lo devuelto en this.myForm
        );
    }
}
