import React from 'react';
import { ModalController, ModalPrototype } from '../modal';
import PropTypes from 'prop-types';

ModalOpener.propTypes = {
    buttonText: PropTypes.string.isRequired,//Nombre
    children: PropTypes.element.isRequired
}

export class ModalOpener extends ModalController {
    
    render() {
        return (
            <div>
                <button onClick={this.showModal}>
                    {this.props.buttonText}
                </button>
                <ModalPrototype show={this.state.showModal} handleClose={this.hideModal} children={this.props.children} />
            </div>
        );
    }
}
