import React, { Component } from 'react';
import { Modal } from './ModalRegDrop';

export class Parent extends Component {

    state = {
        show: false,
        drop: false,
        registration: false,
        showModal: false
    };

    showModal = () => {
        this.setState({ showModal: true });
    };

    hideModal = () => {
        this.setState({ showModal: false });
    };

    changeStates = registration => {
        let A = this.state.show, B = registration, C = this.state.registration;
        let show = !((A && B && C) || (A && !B && !C));
        this.setState({ show, registration, drop: !registration });
    }

    render() {
        return (
            <div>
                <button style={{ "visibility": this.state.show ? "visible" : "hidden" }}
                    onClick={this.showModal}>
                    Talleres
                </button>
                <button style={{ "visibility": this.state.show ? "visible" : "hidden" }}>Art√≠culo</button>
                <RegistrationDropButton changeValue={this.changeStates} />
                <Modal handleClose={this.hideModal} show={this.state.showModal} />
            </div>
        );
    }
}


export class RegistrationDropButton extends Component {

    changeValue = event => {
        this.props.changeValue(event.target.value === 'true');
    }

    render() {
        return (
            <div>
                <button onClick={this.changeValue} value={true}>
                    Altas
                </button>
                <button onClick={this.changeValue} value={false}>
                    Bajas
                </button>
            </div>
        );
    }
}
