import PropTypes from 'prop-types';
import Form from 'react-bootstrap/Form'

export const NameRequest = ({ onChange, name, label }) => {//Pedirá un dato con un input tipo text
    return (
        <Form.Group className="mb-3">
            <Form.Label>Nombre del taller</Form.Label>
            <Form.Control type="text" placeholder="Ingrese el nombre del/la tallerista…" onChange={this.prop.func} />
        </Form.Group>
    );
}


TextInput.propTypes = {
    onChange: PropTypes.func,//Función a ejecutar cuando se modifica el input
    name: PropTypes.string.isRequired,//Nombre de la clase que va a tener el div
    label: PropTypes.string.isRequired//Mostrado en la label
}
