import React, {useState} from 'react';
import PropTypes from 'prop-types';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import Dropdown from 'react-bootstrap/Dropdown';

export const Request = ({ onChange, toShow, label }) => {//Pedirá un dato con un input tipo text

    let placeholder, auxStorage = label;

    switch (toShow) {
        case 'name':
            label = 'Nombre del taller';
            placeholder = 'Ingrese el nombre del/la tallerista';
            break;
        case 'article':
            label = 'Número de artículo';
            placeholder = 'Ingrese el código único del artículo';
            break;
        case 'quantityArticle':
            label = 'Cantidad asignada';
            placeholder = 'Ingrese la cantidad de artículos';
            break;
        case 'task':
            label = 'Número de tarea';
            placeholder = 'Ingrese el número de tarea';
            break;
        case 'money':
            label = 'Monto';
            placeholder = 'Ingrese el monto a pagar';
            break;
        case 'price':
            label = 'Precio';
            placeholder = 'Ingrese el precio por unidad';
            break;
        case 'description':
            label = 'Descripción';
            placeholder = 'Ingrese una breve descripción del artículo';
            break;
        default:
            console.log("ERROR, HA INGRESADO EL toShow " + toShow + " EN EL COMPONENTE TEXTINPUT");
    }
    label = (auxStorage) ? auxStorage : label;
    placeholder += '…';

    return (
        <Form.Group className="mb-3">
            <Form.Label>{label}</Form.Label>
            <InputGroup>
                {(toShow === 'money') ? <InputGroup.Text>$</InputGroup.Text> : <></>}
                <FormControl type="text" placeholder={placeholder} onChange={onChange} />
                {(toShow === 'money') ? <InputGroup.Text>.00</InputGroup.Text> : <></>}
            </InputGroup>
        </Form.Group>
    );
}
Request.propTypes = {
    onChange: PropTypes.func.isRequired, //Función a ejecutar cuando se modifica el input
    toShow: PropTypes.string.isRequired,
    label: PropTypes.string
}

// The forwardRef is important!!
// Dropdown needs access to the DOM node in order to position the Menu
const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
    <a
      href=""
      ref={ref}
      onClick={(e) => {
        e.preventDefault();
        onClick(e);
      }}
    >
      {children}
      &#x25bc;
    </a>
  ));
  
  // forwardRef again here!
  // Dropdown needs access to the DOM of the Menu to measure it
  const CustomMenu = React.forwardRef(
    ({ children, style, className, 'aria-labelledby': labeledBy }, ref) => {
      const [value, setValue] = useState('');
  
      return (
        <div
          ref={ref}
          style={style}
          className={className}
          aria-labelledby={labeledBy}
        >
          <FormControl
            autoFocus
            className="mx-3 my-2 w-auto"
            placeholder="Type to filter..."
            onChange={(e) => setValue(e.target.value)}
            value={value}
          />
          <ul className="list-unstyled">
            {React.Children.toArray(children).filter(
              (child) =>
                !value || child.props.children.toLowerCase().startsWith(value),
            )}
          </ul>
        </div>
      );
    },
  );
  
  render(
    <Dropdown>
      <Dropdown.Toggle as={CustomToggle} id="dropdown-custom-components">
        Custom toggle
      </Dropdown.Toggle>
  
      <Dropdown.Menu as={CustomMenu}>
        <Dropdown.Item eventKey="1">Red</Dropdown.Item>
        <Dropdown.Item eventKey="2">Blue</Dropdown.Item>
        <Dropdown.Item eventKey="3" active>
          Orange
        </Dropdown.Item>
        <Dropdown.Item eventKey="1">Red-Orange</Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>,
  );
