import React from 'react';

export const Modal = ({ handleClose, show, children }) => {
    return (
        <div style={{ 'display': show ? 'block' : 'none' }}>
            <section>
                <button type="button" onClick={handleClose}>
                    Close
                </button>
            </section>
            {children}
        </div>
    );
};
