import React, {Component} from 'react';
import any, {Modal} from './ModalRegDrop';

export class Parent extends Component{
    
    state = {
        show: true,
        drop: false,
        registration: false,
        showModal: false
    }
    
    changeStates = registration =>{
        console.log(this.state);
        this.setState({registration: !!registration, drop: !registration});
    }

    
    
    render(){
        return(
            <div>
                <button style={{"visibility" : this.state.show? "visible" : "hidden"}}
                        onClick={this.setState({showModal: true})}>
                    Talleres
                </button>
                <button style={{"visibility" : this.state.show? "visible" : "hidden"}}>Art√≠culo</button>
                <RegistrationDropButton changeValue = {this.changeStates} />
                <Modal show = {this.showModal}/>
            </div>
        );
    }
}


export class RegistrationDropButton extends Component{
    changeValue = event => {
        this.props.changeValue(event.target.value);
    }

    render(){
        return(
            <div>
                <button onClick={this.changeValue} value={true}>
                    Altas
                </button>
                <button onClick={this.changeValue} value={false}>
                    Bajas
                </button>
            </div>
        );
    }
}
