import React, { Component } from 'react';

export class ModalController extends Component{
    
    state = {
        showModal: false
    }

    showModal = () => {
        this.setState({ showModal: true });
    }

    hideModal = () => {
        this.setState({ showModal: false });
    }

}

export class Modal extends Component{
    

