import React, {Component} from 'react';

export class ModalController{

    state = {
        showModal: false
    }

    showModal = () => {
        this.setState({ showModal: true });
    }

    hideModal = () => {
        this.setState({ showModal: false });
    }


}

export const ModalPrototype = ({handleClose, show, children}) => {
    return (
        <div style={{ 'display': show ? 'block' : 'none', 'background': 'blue' }}>
            <section>
                <button type="button" onClick={handleClose}>
                    Close
                </button>
            </section>
            {children}
        </div>
    );
}
