import React, { useState } from 'react';
import PropTypes from 'prop-types';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import Dropdown from 'react-bootstrap/Dropdown';

export const Request = ({ onChange, toShow, label }) => {//Pedirá un dato con un input tipo text

    let placeholder, auxStorage = label;

    switch (toShow) {
        case 'name':
            label = 'Nombre del taller';
            placeholder = 'Ingrese el nombre del/la tallerista';
            break;
        case 'article':
            label = 'Número de artículo';
            placeholder = 'Ingrese el código único del artículo';
            break;
        case 'quantityArticle':
            label = 'Cantidad asignada';
            placeholder = 'Ingrese la cantidad de artículos';
            break;
        case 'task':
            label = 'Número de tarea';
            placeholder = 'Ingrese el número de tarea';
            break;
        case 'money':
            label = 'Monto';
            placeholder = 'Ingrese el monto a pagar';
            break;
        case 'price':
            label = 'Precio';
            placeholder = 'Ingrese el precio por unidad';
            break;
        case 'description':
            label = 'Descripción';
            placeholder = 'Ingrese una breve descripción del artículo';
            break;
        default:
            console.log("ERROR, HA INGRESADO EL toShow " + toShow + " EN EL COMPONENTE TEXTINPUT");
    }
    label = (auxStorage) ? auxStorage : label;
    placeholder += '…';
    return((toShow === 'name') ? <NameRequest label={label} placeholder={placeholder}/> :
            <Form.Group className="mb-3">
                <Form.Label>{label}</Form.Label>
                <InputGroup>
                    {(toShow === 'money') ? <InputGroup.Text>$</InputGroup.Text> : <></>}
                    <FormControl type="text" placeholder={placeholder} onChange={onChange} />
                    {(toShow === 'money') ? <InputGroup.Text>.00</InputGroup.Text> : <></>}
                </InputGroup>
            </Form.Group>
            );
}
Request.propTypes = {
    onChange: PropTypes.func.isRequired, //Función a ejecutar cuando se modifica el input
    toShow: PropTypes.string.isRequired,
    label: PropTypes.string
}

export const NameRequest = ({label, placeholder}) => {

    const [list, setValue] = useState('');

    return (
        <Form.Group className="mb-3">
            <Form.Label>{label}</Form.Label>
            <InputGroup>
                <DropdownWithFilter list={list} />
            </InputGroup>
        </Form.Group>
    );
}

const DropdownWithFilter = ({ list }) => {
    const CustomMenu = React.forwardRef(
        ({ children }) => {
            const [value, setValue] = useState('');
  
            return (
                <div>
                    <FormControl
                        onChange={(e) => setValue(e.target.value)}
                        value={value}
                    />
                    <ul>
                        {React.Children.toArray(children).filter(
                            (child) =>
                                !value || child.props.children.toLowerCase().startsWith(value.toLowerCase()),
                        )}
                    </ul>
                </div>
            );
        },
    );

    return(
        <Dropdown>
            <Dropdown.Toggle>
                Custom toggle
            </Dropdown.Toggle>
  
            <Dropdown.Menu as={CustomMenu}>
                <Dropdown.Item>Red</Dropdown.Item>
                <Dropdown.Item>Blue</Dropdown.Item>
                <Dropdown.Item>Orange</Dropdown.Item>
            </Dropdown.Menu>
        </Dropdown>
    );

}
