    data: '',
    table: '',
    titles: '',
};

export class ShowData extends Component {

    state = initialState;

    resetState = () => {
        this.setState(initialState);
    }

    setData = e => {
        console.log('http://localhost:3001/get' + e.charAt(0).toUpperCase() + e.slice(1));
