import React, { Component } from 'react';
import { ModalOpener } from './modalOpener';
import { Request } from '../textInputs';
import image from './Images/NewPayment.svg';
import Axios from 'axios';
import Form from 'react-bootstrap/Form';

export class NewPaymentButton extends Component {
    state = {
        name: '',
        quantity: ''
    }

    ex = (element) => {
        this.setState({ name: element });
    }
    myForm = () => {
        return (
            <Form>
                <Request toShow="name" onChange={this.ex(event)} />

                <Request toShow="money" onChange={(event) => {
                    this.setState({ quantity: event.target.value });
                }} />
            </Form>
        );
    }

    post = () => {
        Axios.post('http://localhost:3001/newPayment',
            { date: Date.now() - 1644308403717, name: this.state.name, quantity: this.state.quantity });
    }

    render() {
        return (
            <ModalOpener buttonText='Ingresar pago' children={this.myForm()}
                className={'title'} logo={image} title={'Ingreso de pago'} post={this.post} />
            //Crea un bot칩n que abre a un modal en el que aparecer치 lo devuelto en this.myForm
        );
    }
}
