import React, { Component } from 'react';
import { Modal, Article, Workshop } from './Modal';

export class Parent extends Component {

    state = {
        show: false,//Boolean ¿Mostramos los botones de alta y baja?
        registration: false,//Boolean ¿Le estamos dando de alta a algo? De lo contrario, es la baja
        showModal: false,//Boolean ¿Mostramos el modal?
        article: false//Boolean ¿Se está dando de alta/baja un artículo? De lo contrario, es un taller
    }

    showModal = e => {
        this.setState({ showModal: true, article: e === 'Art' });
    }

    hideModal = () => {
        this.setState({ showModal: false });
    }

    changeStates = registration => {
        let A = this.state.show, B = registration, C = this.state.registration;
        let show = !((A && B && C) || (A && !B && !C));
        this.setState({ show, registration });
    }


    render() {
        return (
            <div>
                <button style={{ "visibility": this.state.show ? "visible" : "hidden" }}
                    onClick={this.showModal} value={'Work'}>
                    Talleres
                </button>
                <button style={{ "visibility": this.state.show ? "visible" : "hidden" }}
                    onClick={this.showModal} value={'Art'}>
                    Artículo
                </button>
                <RegistrationDropButton changeValue={this.changeStates} />
                <Modal handleClose={this.hideModal} show={this.state.showModal} />
            </div>
        );
    }
}


export class RegistrationDropButton extends Component {

    changeValue = event => {
        this.props.changeValue(event.target.value === 'true');
    }

    render() {
        return (
            <div>
                <button onClick={this.changeValue} value={true}>
                    Altas
                </button>
                <button onClick={this.changeValue} value={false}>
                    Bajas
                </button>
            </div>
        );
    }
}
