import React, { useState } from 'react';
import { Article } from './articleRegDrop';
import { Workshop } from './workshopRegDrop';
import { ModalPrototype } from '../../modal'
import PropTypes from 'prop-types';

export const ModalArt = ({ handleClose, show, registration }) => {//Se encargará de los forms para dar de alta/baja a los artículos

    const [id, setId] = useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');
    const [article, setArticle] = useState('');
    
    const post = () => {
        if(registration)
            Axios.post('http://localhost:3001/create',
                {id, price, description});
        else
            Axios.post('http://localhost:3001/create',
                    {article});
    }

    const MyForm = () =>{
        if (registration) {//de ser true, se está dando el alta, de lo contrario, la baja
            return (
                <Form>
                    <Form.Group className="mb-3">
                        <Form.Label>Número de Artículo</Form.Label>
                        <Form.Control type="text" placeholder="Ingrese el número único…" onChange={(event) => {
                            setId(event.target.value);
                        }} />
                    </Form.Group>
    
                    <Form.Group className="mb-3">
                        <Form.Label>Precio por unidad</Form.Label>
                        <Form.Control type="text" placeholder="Ingrese el precio…" onChange={(event) => {
                            setPrice(event.target.value);
                        }} />
                    </Form.Group>
    
                    <Form.Group className="mb-3">
                        <Form.Label>Descripción</Form.Label>
                        <Form.Control type="text" placeholder="Ingrese una descripción del artículo…" onChange={(event) => {
                            setDescription(event.target.value);
                        }} />
                    </Form.Group>
                </Form>
            );
        }
        else
            return (
                <Form>
                    <Form.Group className="mb-3">
                        <Form.Label>Número de artículo</Form.Label>
                        <Form.Control type="text" placeholder="Ingrese el código del artículo…" onChange={(event) => {
                            setArticle(event.target.value);
                        }} />
                    </Form.Group>
                </Form>
            );
    }

    return (
        <ModalPrototype show={show} handleClose={handleClose} children={MyForm}
            title={(registration) ? 'Alta' : 'Baja' + 'de artículo'} post={post} />//El modal debe mostrar el form especificado en Article
    );

}

ModalArt.propTypes = {
    handleClose: PropTypes.func.isRequired, //Función que cierra el modal
    show: PropTypes.bool.isRequired, //True si y sólo si se debe o no ver el modal
    registration: PropTypes.bool.isRequired //True si y sólo si se da un alta
}

export const ModalWork = ({ handleClose, show, registration, post }) => {//Se encargará de los forms para dar de alta/baja a los talleres
    return (
        <ModalPrototype show={show} handleClose={handleClose} children={<Workshop registration={registration} />}
            title={(registration) ? 'Alta' : 'Baja' + 'de taller'} post={post} />//El modal debe mostrar el form especificado en Workshop
    );
}

ModalWork.propTypes = ModalArt.propTypes;
