import React, {Component} from 'react';
import ReactDOM from 'react-dom';

export class Parent extends Component{
    

    state = {
        show: false,
        drop: false,
        registration: false
    }

    
    changeStates = registration =>{
        let show = !((this.state.show && registration && this.state.registration) ||
                     (this.state.show && !registration && !this.state.registration));
        console.log(this.state.show+ ", " + registration + ", " + this.state.registration);
        this.setState({show, registration, drop: !registration});
        console.log(show);
    }

    
    
    render(){
        return(
            <div>
                <button style={{"visibility" : this.state.show? "visible" : "hidden"}}>Talleres</button>
                <button style={{"visibility" : this.state.show? "visible" : "hidden"}}>Art√≠culo</button>
                <RegistrationDropButton changeValue = {this.changeStates} />
            </div>
        );
    }
}


export class RegistrationDropButton extends Component{
    onClick = event => {
        this.props.changeValue(event.target.value)
    }

    render(){

        return(
            
            <div>
                <button onClick={this.onClick} value={true}>
                    Altas
                </button>
                <button onClick={this.onClick} value={false}>
                    Bajas
                </button>
            </div>
        );
    }
}
