import React, { useState, useEffect, Component, Fragment } from 'react';
import PropTypes from 'prop-types';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import Popover from 'react-bootstrap/Popover';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger'
import Axios from 'axios';
import Button from 'react-bootstrap/Button';
import { ModalArt, ModalWork } from './Menu/RegistrationDrop/modals'
import './autocomplete.css';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Table from 'react-bootstrap/Table'
import moment from 'moment';

export const Request = ({ onChange, toShow, label }) => {//Pedirá un dato con un input tipo text

    let placeholder, auxStorage = label;

    switch (toShow) {
        case 'name':
            label = 'Nombre del taller';
            placeholder = 'Ingrese el nombre del/la tallerista';
            break;
        case 'article':
            label = 'Número de artículo';
            placeholder = 'Ingrese el código único del artículo';
            break;
        case 'quantityArticle':
            label = 'Cantidad asignada';
            placeholder = 'Ingrese la cantidad de artículos';
            break;
        case 'task':
            label = 'Número de tarea';
            placeholder = 'Ingrese el número de tarea';
            break;
        case 'money':
            label = 'Monto';
            placeholder = 'Ingrese el monto a pagar';
            break;
        case 'price':
            label = 'Precio por unidad';
            placeholder = 'Ingrese el precio por unidad';
            break;
        case 'description':
            label = 'Descripción';
            placeholder = 'Ingrese una breve descripción del artículo';
            break;
        case 'regName':
            label = 'Nombre del taller';
            placeholder = 'Ingrese el nombre del/la tallerista';
            break;
        case 'regArticle':
            label = 'Número de artículo';
            placeholder = 'Ingrese el código del artículo';
            break;
        case 'weight':
            label = 'Peso de la tarea';
            placeholder = 'Ingrese el peso entregado';
            break;
        default:
            console.log("ERROR, HA INGRESADO EL toShow " + toShow + " EN EL COMPONENTE TEXTINPUT");
    }
    label = (auxStorage) ? auxStorage : label;
    placeholder += '…';
    return ((toShow === 'name') ? <NameRequest label={label} placeholder={placeholder} onChange={onChange} /> :
        ((toShow === 'article') ? <ArticleRequest label={label} placeholder={placeholder} onChange={onChange} /> :
            <Form.Group className="mb-3">
                <Form.Label>{label}</Form.Label>
                <InputGroup>
                    {(toShow === 'money') ? <InputGroup.Text>$</InputGroup.Text> : <></>}
                    <FormControl type="text" placeholder={placeholder} onChange={onChange} />
                    {(toShow === 'money') ? <InputGroup.Text>.00</InputGroup.Text> : <></>}
                </InputGroup>
            </Form.Group>)
    );
}

Request.propTypes = {
    onChange: PropTypes.func.isRequired, //Función a ejecutar cuando se modifica el input
    toShow: PropTypes.string.isRequired,
    label: PropTypes.string
}

export const TaskRequest = ({ onChange, name }) => {

    const [tasks, setTasks] = useState([]);
    const [title, setTitle] = useState('Elegir tarea');
    const [selectedTask, setSelectedTask] = useState('');
    const [text, setText] = useState('');
    const [parts, setParts] = useState([]);
    const [input, setInput] = useState({quantity: 0, weight: 0, money: 0});

    useEffect(() => {
        if (name === '')
            return;
        Axios.post('http://localhost:3001/getTasks', { name }).then((response) => {
            if (response.data.length === 0)
                return;
            setTasks(response.data);
        })
    }, [name]);

    useEffect(() => {
        Axios.post('http://localhost:3001/getParts', { task: selectedTask.id }).then((response) => 
            setParts(response.data)
        );
    }, [selectedTask]);

    const getTable0 = () => {
        let aux1 = 10;
        return (
            <>
            <Button onClick={() => {
                let aux = {...input};
                aux.quantity = aux.quantity + 1;
                setInput({...aux});
                console.log(input);
            }}> UP </Button>
            <h1>{aux1 + input.quantity}</h1>
            </>
        )
    }
    const getTable = () => {

        let total = {
            quantity: selectedTask.quantity,
            weight: selectedTask.weight,
            money: selectedTask.quantity * selectedTask.price
        };
        return (
            <>
                {parts.map((part, index) => {
                    total = {
                        quantity: total.quantity - part.quantity,
                        weight: total.weight - part.weight,
                        money: total.money - part.money
                    };

                    return (
                        <tr key={index}>
                            <td>{part.date}</td>
                            <td>{part.quantity}</td>
                            <td>{total.quantity}</td>
                            <td>{part.weight}</td>
                            <td>{total.weight}</td>
                            <td>{part.money}</td>
                            <td>{total.money}</td>
                        </tr>
                    );
                })}
                <tr>

                    <td>{moment(new Date()).format("DD/MM/YYYY")}</td>

                    <td><FormControl value={text} onChange={(e) => {
                        setText(e.target.value);
                        let aux = {...input};
                        aux.quantity = (total.quantity < e.target.value)? total.quantity : e.target.value;
                        setInput({...aux});
                    }} /></td>

                    <td>{total.quantity - input.quantity}</td>

                    <td><FormControl onChange={(e) => {
                        let aux = {...input};
                        aux.weight = e.target.value;
                        setInput({...aux});
                    }} /></td>
                    <td>{total.weight - input.weight}</td>

                    <td><FormControl onChange={(e) => {
                        let aux = {...input};
                        aux.money = e.target.value;
                        setInput({...aux});
                    }} /></td>

                    <td>{total.money - input.money}</td>

                </tr>
            </>
        )
    }

    return (
        <>
            <DropdownButton title={title} onSelect={(e) => {
                setTitle('Tarea ' + tasks[e].id);
                setSelectedTask(tasks[e]);
            }}>
                {tasks.map((task, index) => {
                    return (
                        <Dropdown.Item key={task.id} eventKey={index}>
                            {task.id + ": " + task.article_id + " (" + task.article_description + ") x" + task.quantity}
                        </Dropdown.Item>
                    );
                })}
            </DropdownButton>
            {title === 'Elegir tarea' ? <></> :
                <Table striped bordered>
                    <thead>
                        <tr>
                            <th>Fecha</th>
                            <th>Cantidad entregada</th>
                            <th>Cantidad total</th>
                            <th>Peso entregado</th>
                            <th>Peso total</th>
                            <th>Dinero entregado</th>
                            <th>Dinero total</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>{selectedTask.date}</td>
                            <td></td>
                            <td>{selectedTask.quantity}</td>
                            <td></td>
                            <td>{selectedTask.weight}</td>
                            <td></td>
                            <td>{selectedTask.price * selectedTask.quantity}</td>
                        </tr>
                        {getTable()}
                    </tbody>
                </Table>}
        </>
    );
}

const ArticleRequest = ({ label, placeholder, onChange }) => {

    const [id, setId] = useState('');
    const [description, setDescription] = useState('');
    const [error, setError] = useState(false);
    const [show, setShow] = useState(false);

    const getDescription = (userInput) => {
        Axios.post('http://localhost:3001/getDescriptionWhere', { id: userInput }).then((response) => {
            setError(response.data.length === 0);
            if (response.data.length !== 0)
                setDescription(response.data[0].description);
            else
                setDescription('');
        });
    }

    const popover = (
        <Popover>
            <Popover.Header as="h3">Descripción de artículo</Popover.Header>
            <Popover.Body>
                {error ?
                    <em>
                        <p>No existe este código de artículo</p>
                        <Button onClick={() => {
                            setId('');
                            setShow(true);
                        }}>Ingresar</Button>
                    </em>
                    : description}
            </Popover.Body>
        </Popover >
    );


    if (!show)
        return (
            <Form.Group className="mb-3">
                <Form.Label>{label}</Form.Label>
                <InputGroup>
                    <OverlayTrigger show={id !== ''} placement="right" overlay={popover}>
                        <FormControl type="text" placeholder={placeholder} onChange={(e) => {
                            onChange(e);
                            setId(e.target.value)
                            getDescription(e.target.value);
                        }} />
                    </OverlayTrigger>
                </InputGroup>
            </Form.Group>);
    return (<ModalArt handleClose={() => setShow(false)} show={show} registration={true} />);
}

ArticleRequest.propTypes = {
    label: PropTypes.string.isRequired,
    placeholder: PropTypes.string.isRequired,
    onChange: PropTypes.func.isRequired
}

const NameRequest = ({ label, placeholder, onChange }) => {

    const [list, setList] = useState([]);

    useEffect(() => {
        getList();
    }, []);
    const getList = () => {
        Axios.get('http://localhost:3001/getNames').then((response) => {
            setList(response.data);
        });
    }
    return (
        <Form.Group className="mb-3">
            <Form.Label>{label}</Form.Label>
            <Autocomplete suggestions={list.map((element) => { return element.name })}
                onChange={onChange} placeholder={placeholder} updateList={getList} />
        </Form.Group>
    );
}

NameRequest.propTypes = ArticleRequest.propTypes;

class Autocomplete extends Component {
    static propTypes = {
        suggestions: PropTypes.instanceOf(Array).isRequired,
        onChange: PropTypes.func.isRequired,
        placeholder: PropTypes.string,
        updateList: PropTypes.func.isRequired
    };

    static defaultProps = {
        placeholder: 'Ingrese el nombre del/la tallerista'
    };

    constructor(props) {
        super(props);

        this.state = {
            // The active selection's index
            activeSuggestion: 0,
            // The suggestions that match the user's input
            filteredSuggestions: [],
            // Whether or not the suggestion list is shown
            showSuggestions: false,
            // What the user has entered
            userInput: "",
            showModal: false
        };
    }

    onChange = e => {
        const { suggestions } = this.props;
        const userInput = e.currentTarget.value;

        // Filter our suggestions that don't contain the user's input
        const filteredSuggestions = suggestions.filter(
            suggestion =>
                suggestion.toLowerCase().indexOf(userInput.toLowerCase()) > -1
        );

        this.setState({
            activeSuggestion: 0,
            filteredSuggestions,
            showSuggestions: true,
            userInput: e.currentTarget.value
        });
        this.props.onChange(e.currentTarget.value);
    };

    onClick = e => {
        this.setState({
            activeSuggestion: 0,
            filteredSuggestions: [],
            showSuggestions: false,
            userInput: e.currentTarget.innerText
        });
        this.props.onChange(e.currentTarget.innerText);
    };

    render() {
        const {
            onChange,
            onClick,
            onKeyDown,
            state: {
                activeSuggestion,
                filteredSuggestions,
                showSuggestions,
                userInput
            }
        } = this;

        let suggestionsListComponent;

        if (showSuggestions && userInput) {
            if (filteredSuggestions.length) {
                suggestionsListComponent = (
                    <ul className="suggestions">
                        {filteredSuggestions.map((suggestion, index) => {
                            let className;

                            // Flag the active suggestion with a class
                            if (index === activeSuggestion) {
                                className = "suggestion-active";
                            }

                            return (
                                <li className={className} key={suggestion} onClick={onClick}>
                                    {suggestion}
                                </li>
                            );
                        })}
                    </ul>
                );
            } else {
                suggestionsListComponent = (
                    <div className="no-suggestions">
                        <em>El taller ingresado no existe </em>
                        <Button onClick={() => {
                            this.setState({
                                showModal: true
                            })

                        }}>Ingresar</Button>
                    </div>
                );
            }
        }

        if (!this.state.showModal)
            return (
                <Fragment>
                    <FormControl
                        placeholder={this.props.placeholder}
                        type="text"
                        onChange={onChange}
                        onKeyDown={onKeyDown}
                        value={userInput}
                    />
                    {suggestionsListComponent}
                </Fragment>
            );
        else
            return (<ModalWork handleClose={() => {
                this.setState({ showModal: false });
                this.props.updateList();
            }}
                show={this.state.showModal} registration={true} />);
    }
}
