import React, { Component } from 'react';
import { ModalArt, ModalWork } from './modal';
import { ModalOpener } from '../modalOpener';
import { ModalController } from '../../modal';

export class ArticleButton extends ModalController{

    state={
        ...this.state,
        registration: false
    }
    showModal = e => {
        this.showModal();
        this.setState({registration: e.target.value === 'true'})
    }
    render(){
        return(
            <div>
                <div>
                    Artículo
                </div>
                <button onClick={this.showModal} value={'true'}>
                    Alta
                </button>
                <button onClick={this.showModal} value={'false'}>
                    Baja
                </button>
                <ModalArt handleClose={this.hideModal} show={this.state.showModal} registration={this.state.registration}/>
            </div>
        );
    }
}

export class WorkshopButton extends ModalController{

    state={
        ...this.state,
        registration: false
    }
    showModal = e => {
        this.showModal();
        this.setState({registration: e.target.value === 'true'})
    }
    render(){
        return(
            <div>
                <div>
                    Artículo
                </div>
                <button onClick={this.showModal} value={'true'}>
                    Alta
                </button>
                <button onClick={this.showModal} value={'false'}>
                    Baja
                </button>
                <ModalWork handleClose={this.hideModal} show={this.state.showModal} registration={this.state.registration}/>
            </div>
        );
    }
}

