import React from 'react';
import { TextInput } from '../../textInput';
import PropTypes from 'prop-types';

Article.propTypes = {
    registration: PropTypes.bool.isRequired //True si y sólo si se da un alta
}

export const Article = ({ registration }) => {
    if (registration)//de ser true, se está dando el alta, de lo contrario, la baja
        return (
            <form>{/*Devuelve el form para dar de alta a un artículo. Pide todos los datos necesarios*/}
                <TextInput name={'Number'} label={'Número de Artículo'} />
                <TextInput name={'Price'} label={'Precio por unidad'} />
                <TextInput name={'Description'} label={'Descripción'} />
            </form>
        );
    else
        return (
            <form>{/*Devuelve el form para dar de baja a un artículo. Pide todos los datos necesarios*/}
                <TextInput name={'Number'} label={'Número de Artículo'} />
            </form>
        );
}
