import React, { Component } from 'react';
import { ModalWorkshop } from './ModalWorkshop';
import { ModalArticle } from './ModalArticle';

export class Parent extends Component {

    state = {
        show: false,
        drop: false,
        registration: false,
        showModalArt: false,
        showModalWork: false
    }

    showModal = event => {
        if (event.target.value === 'Art')
            this.setState({ showModalArt: true });
        else
            this.setState({ showModalWork: true });
    }

    hideModal = e => {
        console.log(e === 'Art');
        if (e === 'Art')
            this.setState({ showModalArt: false });
        else
            this.setState({ showModalWork: false });
    }

    changeStates = registration => {
        let A = this.state.show, B = registration, C = this.state.registration;
        let show = !((A && B && C) || (A && !B && !C));
        this.setState({ show, registration, drop: !registration });
    }

    render() {
        return (
            <div>
                <button style={{ "visibility": this.state.show ? "visible" : "hidden" }}
                    onClick={this.showModal} value={'Work'}>
                    Talleres
                </button>
                <button style={{ "visibility": this.state.show ? "visible" : "hidden" }}
                    onClick={this.showModal} value={'Art'}>
                    Art√≠culo
                </button>
                <RegistrationDropButton changeValue={this.changeStates} />
                <ModalArticle handleClose={this.hideModal('Art')} show={this.state.showModalArt} />
                <ModalWorkshop handleClose={this.hideModal('Work')} show={this.state.showModalWork} />
            </div>
        );
    }
}


export class RegistrationDropButton extends Component {

    changeValue = event => {
        this.props.changeValue(event.target.value === 'true');
    }

    render() {
        return (
            <div>
                <button onClick={this.changeValue} value={true}>
                    Altas
                </button>
                <button onClick={this.changeValue} value={false}>
                    Bajas
                </button>
            </div>
        );
    }
}
