import React, { Component } from 'react';
import { ModalOpener } from './modalOpener';
import { Request, TaskRequest } from '../textInputs';
import Axios from 'axios';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import { ModalPrototype } from '../modal';

const initialState = {
    data: ''
};

export class ShowData extends Component {

    state = initialState;

    resetState = () => {
        this.setState(initialState);
    }


    render() {
        return (
            <DropdownButton title={'Datos a mostrar'} onSelect={(e) => {
                this.setState({data: e});
            }} >
                <Dropdown.Item eventKey="articles">Art√≠culos</Dropdown.Item>
                <Dropdown.Item eventKey="workshops">Talleres</Dropdown.Item>
                <Dropdown.Item eventKey="payments">Pagos</Dropdown.Item>
                <Dropdown.Item eventKey="tasks">Tareas</Dropdown.Item>
            </DropdownButton>
        );
    }
}
