import React, {Component} from 'react';
import ReactDOM from 'react-dom';

export class Parent extends Component{
    

    state = {
        show: new Boolean(0),
        drop: new Boolean(0),
        registration: new Boolean(0)
    }

    
    changeStates = registration =>{
        registration = new Boolean(registration);
        console.log(registration);
        console.log(!registration);
        let A = this.state.show, B = registration, C = this.state.registration;
        let show = !((A && B && C) || (A && !B && !C));
        console.log(A + ", " + B + ", " + C);
        this.setState({show, registration: registration, drop: !registration});
        
        console.log(this.state);
    }

    
    
    render(){
        return(
            <div>
                <button style={{"visibility" : this.state.show? "visible" : "hidden"}}>Talleres</button>
                <button style={{"visibility" : this.state.show? "visible" : "hidden"}}>Art√≠culo</button>
                <RegistrationDropButton changeValue = {this.changeStates} />
            </div>
        );
    }
}


export class RegistrationDropButton extends Component{
    onClick = event => {
        this.props.changeValue(event.target.value);
    }

    render(){

        return(
            
            <div>
                <button onClick={this.onClick} value={1}>
                    Altas
                </button>
                <button onClick={this.onClick} value={0}>
                    Bajas
                </button>
            </div>
        );
    }
}
