import React, { Component } from 'react';
import { ModalArt, ModalWork } from './modal';
import { ModalController } from '../../modal';

export class ArticleButton extends ModalController {

    state = {
        ...this.state,//Hereda los estados de ModalController (showModal: false)
        registration: false//True si y sólo si se está dando el alta al artículo
    }

    showModal = e => {//Cuando se presione el botón se ejecuta
        super.showModal();//Muestra el modal (heredado de ModalController)
        this.setState({ registration: e.target.value === 'true' })//registration toma el valor pasado en el botón
    }

    render() {
        return (
            <div>
                <div>
                    Taller
                </div>
                <button onClick={this.showModal} value={'true'}>
                    Alta
                </button>
                <button onClick={this.showModal} value={'false'}>
                    Baja
                </button>
                <ModalArt handleClose={this.hideModal} show={this.state.showModal} registration={this.state.registration} />
                {/*Llama al Modal encargado de los Artículos*/}
            </div>
        );
    }
}

export class WorkshopButton extends ModalController {

     oldShowModal = showModal;

    state = {
        ...this.state,//Hereda los estados de ModalController (showModal: false)
        registration: false//True si y sólo si se está dando el alta al artículo
    }

    
    showModal = e => {//Cuando se presione el botón se ejecuta
        this.setState({ showModal: true, registration: e.target.value === 'true' })//registration toma el valor pasado en el botón
    }

    render() {
        return (
            <div>
                <div>
                    Artículo
                </div>
                <button onClick={this.showModal} value={'true'}>
                    Alta
                </button>
                <button onClick={this.showModal} value={'false'}>
                    Baja
                </button>
                <ModalWork handleClose={this.hideModal} show={this.state.showModal} registration={this.state.registration} />
                {/*Llama al Modal encargado de los Talleres*/}
            </div>
        );
    }
}
