import { ModalOpener } from './modalOpener';
import { Request } from '../textInputs';
import image from './Images/AsignTask.svg';
import Axios from 'axios';
import Form from 'react-bootstrap/Form';
import moment from 'moment';

export class AsignTaskButton extends Component {
    state = {
        name: '',
        task: '',
        deadline: '',
        weight: '',
        threads: '',
        //Auto-asignar
        date: '',
        packages: '',
        description: '',
        quantity: '',
    }


    myForm = () => {
        
        return (
            <Request toShow="name" onChange={(event) => {
                this.setState({ name: event.target.value });
            }} />
        );
    }

    post = () => {
        
    }
    
    render() {
        return (
            <ModalOpener buttonText='Taller' children={this.myForm()}
                className={'title'} logo={image} title={'Asignar Tarea'} post={this.post} />
            //Crea un bot칩n que abre a un modal en el que aparecer치 lo devuelto en this.myForm
        );
    }
}
