import React from 'react';

export const Modal = ({ handleClose, show, article, registration }) => {
    return (
        <div style={{ 'display': show ? 'block' : 'none', 'background': 'blue' }}>
            <section>
                <button type="button" onClick={handleClose}>
                    Close
                </button>
            </section>
            <p>
                {article ? 'true' : 'false'}
            </p>
            {chooseForm(article, registration)}
        </div>
    );
}

function chooseForm(article, registration) {
    console.log(article);
    if (article)
        return <Article registration={registration} />
    return <Workshop registration={registration} />
}

const Article = ({ registration }) => {
    if (registration)
        return (
            <form>
                <input>Número de artículo</input>
                <input>Precio por unidad</input>
                <input>Descripción</input>
            </form>
        );
    else
        return (
            <form>
                <input>Número de artículo</input>
            </form>
        );
}

const Workshop = ({ registration }) => {
    if(registration)
        return (
            <form>
                <input>Nombre</input>
                <input>Datos de contacto</input>
            </form>
        );
    else
        return(
            <form>
                <input>Nombre</input>
            </form>
        );
}

function example(registration) {
    return (registration ? ' el alta ' : ' la baja ');
}
