import React, { Component } from 'react';
import { ArticleButton, WorkshopButton } from './RegistrationDrop/registrationDrop';
import { NewPaymentButton } from './newPayment'
import { NewTaskButton } from './newTask';
import { NavegationButton } from './navegation';
import { DeliverTask } from './deliverTask';
import './styles.css';

export default class Menu extends Component {
  render() {
    return (
      <>
        <ul className='menu-selector'>
          <li><NewPaymentButton /></li>
          <li><NewTaskButton /></li>
          <li><NavegationButton /></li>
          <li><DeliverTask /></li>
          <li><WorkshopButton /></li>
          <li><ArticleButton /></li>
        </ul>
      </>
    );
  }
}

const App1 = () => {
  const [isOpen, setIsOpen] = React.useState(false);

  const showModal = () => {
    setIsOpen(true);
  };

  const hideModal = () => {
    setIsOpen(false);
  };

  return (
    <>
      <button onClick={showModal}>Display Modal</button>
      <Modal show={isOpen} onHide={hideModal}>
        <Modal.Header>
          <Modal.Title>Hi</Modal.Title>
        </Modal.Header>
        <Modal.Body>The body</Modal.Body>
        <Modal.Footer>
          <button onClick={hideModal}>Cancel</button>
          <button>Save</button>
        </Modal.Footer>
      </Modal>
    </>
  );
};
