import React, { Component, useState, useEffect } from 'react';
import { ModalOpener } from './modalOpener';
import { Request } from '../textInputs';
import Form from 'react-bootstrap/Form';
import image from './Images/DeliverTask.svg';
import moment from 'moment';
import Axios from 'axios';
import Table from 'react-bootstrap/Table';
import FormControl from 'react-bootstrap/FormControl';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';


export class DeliverTask extends Component {
    state = {
        task: '',
        quantity: '',
        money: '',
        weight: '',
        spare: false,
        error: false,
    }

    changeValue = (state, val) => {
        this.setState({ [state]: val });
    }

    myForm = () => {
        return (
            <Form id="toPrint">
                <Request toShow="name" onChange={(element) => {
                    this.setState({ name: element });
                }} />
                <TaskRequest name={this.state.name} onChange={(data, task, error) => {
                    this.setState({ task, quantity: data.quantity, money: data.money, weight: data.weight, error });
                }} />
            </Form>
        );
    }

    post = () => {
        Axios.post('http://localhost:3001/newPart',
            {
                task: this.state.task, date: moment(new Date()).format('DD/MM/YYYY'), quantity: this.state.quantity,
                weight: this.state.weight.toFixed(1), money: this.state.money.toFixed(1)
            });
    }

    print = () => {
        var originalContents = document.body.innerHTML;

        document.body.innerHTML = document.getElementById('toPrint').innerHTML;

        window.print();

        document.body.innerHTML = originalContents;
    }

    render() {
        return (
            <ModalOpener buttonText='Recibir tarea' children={this.myForm()} logo={image} className={'title'}
                footer={{ label: 'Imprimir', func: this.print, show: document.getElementById("taskTable") }}
                title={'Ingreso de tarea'} post={this.post} error={this.state.error} />
            //Crea un bot칩n que abre a un modal en el que aparecer치 lo devuelto en this.myForm
        );
    }
}

export const TaskRequest = ({ onChange, name }) => {

    const [tasks, setTasks] = useState([]);
    const [title, setTitle] = useState('Elegir tarea');
    const [parts, setParts] = useState([]);
    const [input, setInput] = useState({ quantity: 0, weight: 0, money: 0 });
    const [selectedTask, setSelectedTask] = useState('');

    useEffect(() => {
        if (name === '')
            return;
        Axios.post('http://localhost:3001/getTasks', { name }).then((response) => {
            if (response.data.length === 0)
                return;
            setTasks(response.data);
        })
    }, [name]);

    useEffect(() => {
        Axios.post('http://localhost:3001/getParts', { task: selectedTask.id }).then((response) =>
            setParts(response.data)
        );
    }, [selectedTask]);

    useEffect(() => {
        onChange(input, selectedTask.id,
            (input.quantity > 0 && input.weight > 0 && input.money >= 0));
    }, [input]); // eslint-disable-line react-hooks/exhaustive-deps

    const inputField = (property, total) => {
        return (
            <FormControl value={input[property]} onChange={(e) => {

                if (isNaN(e.target.value) || total[property] < e.target.value)
                    return;
                let aux = { ...input };
                aux[property] = e.target.value;
                aux[property] = (aux[property] < 0) ? 0 : aux[property];
                if (property === 'quantity')
                    aux[property] = Math.round(aux[property]);
                setInput({ ...aux });
            }} />
        );
    }

    const getTable = () => {

        let total = {
            quantity: selectedTask.quantity,
            weight: selectedTask.weight,
            money: selectedTask.quantity * selectedTask.price
        };
        return (
            <>
                {parts.map((part, index) => {
                    total = {
                        quantity: total.quantity - part.quantity,
                        weight: total.weight - part.weight,
                        money: total.money - part.money
                    };

                    return (
                        <tr key={index}>
                            <td>{part.date}</td>
                            <td>{part.quantity}</td>
                            <td>{total.quantity}</td>
                            <td>{part.weight}</td>
                            <td>{total.weight}</td>
                            <td>{'$' + part.money}</td>
                            <td>{'$' + total.money}</td>
                        </tr>
                    );
                })}
                <tr>
                    <td>{moment(new Date()).format('DD/MM/YYYY')}</td>
                    <td>{inputField('quantity', total)}</td>
                    <td>{total.quantity - input.quantity}</td>
                    <td>{inputField('weight', total)}</td>
                    <td>{(total.weight - input.weight).toFixed(1)}</td>
                    <td>{inputField('money', total)}</td>
                    <td>{'$' + (total.money - input.money).toFixed(1)}</td>
                </tr>
            </>
        )
    }

    return (
        <>
            <div style={{ 'justifyContent': 'space-between', 'display': 'flex', 'alignItems': 'baseline' }}>
                <DropdownButton title={title} onSelect={(e) => {
                    setTitle('T' + tasks[e].id + ': ' + tasks[e].article_id + ' (' + tasks[e].article_description + ') x' + tasks[e].quantity + ' a $' + tasks[e].price);
                    setSelectedTask(tasks[e]);
                }}>
                    {tasks.map((task, index) => {
                        return (
                            <Dropdown.Item key={task.id} eventKey={index}>
                                {task.id + ': ' + task.article_id + ' (' + task.article_description + ') x' + task.quantity + ' a $' + task.price}
                            </Dropdown.Item>
                        );
                    })}
                </DropdownButton>
                <h4>
                    {(selectedTask === '') ? null : selectedTask.deadline}
                </h4>
            </div>
            {title === 'Elegir tarea' ? <></> :
                <Table striped bordered id="taskTable">
                    <thead>
                        <tr>
                            <th>Fecha</th>
                            <th>Cantidad entregada</th>
                            <th>Cantidad total</th>
                            <th>Peso entregado</th>
                            <th>Peso total</th>
                            <th>Dinero entregado</th>
                            <th>Dinero total</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>{selectedTask.date}</td>
                            <td></td>
                            <td>{selectedTask.quantity}</td>
                            <td></td>
                            <td>{selectedTask.weight}</td>
                            <td></td>
                            <td>{'$' + selectedTask.price * selectedTask.quantity}</td>
                        </tr>
                        {getTable()}
                    </tbody>
                </Table>}
        </>
    );
}
