import React, { useState, useEffect, Component, Fragment } from 'react';
import PropTypes from 'prop-types';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import Popover from 'react-bootstrap/Popover';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger'
import Axios from 'axios';
import Button from 'react-bootstrap/Button';
import { ModalArt, ModalWork } from './Menu/RegistrationDrop/modals'
import './autocomplete.css';

export const Request = ({ onChange, toShow, label }) => {//Pedirá un dato con un input tipo text

    const [error, setError] = useState(false);
    const [defValue, setDefValue] = useState(undefined);

    let placeholder, auxStorage = label, checked = (input) => input !== '';


    switch (toShow) {
        case 'name':
            label = 'Nombre del taller';
            placeholder = 'Ingrese el nombre del/la tallerista';
            break;
        case 'article':
            label = 'Número de artículo';
            placeholder = 'Ingrese el código único del artículo';
            break;
        case 'quantityArticle':
            label = 'Cantidad asignada';
            placeholder = 'Ingrese la cantidad de artículos';
            checked = (input) => !isNaN(input) && Number(input) > 0 && Number(input) === Math.round(Number(input));
            break;
        case 'task':
            label = 'Número de tarea';
            console.log("trying");
            Axios.get('http://localhost:3001/getTaskCount').then(response => {
                
                let res = response.data[0];
                console.log("trying again");
                Axios.delete(`http://localhost:3001/deleteTask/${res}`).then(() => {
                    setDefValue(res);
                });
            });
            checked = (input) => Number(input) > defValue && !isNaN(input) && Number(input) === Math.round(Number(input));
            break;
        case 'money':
            label = 'Monto';
            placeholder = 'Ingrese el monto a pagar';
            checked = (input) => !isNaN(input) && Number(input) > 0;
            break;
        case 'price':
            label = 'Precio por unidad';
            placeholder = 'Ingrese el precio por unidad';
            checked = (input) => !isNaN(input) && Number(input) > 0;
            break;
        case 'description':
            label = 'Descripción';
            placeholder = 'Ingrese una breve descripción del artículo';
            break;
        case 'regName':
            label = 'Nombre del taller';
            placeholder = 'Ingrese el nombre del/la tallerista';
            // checked = (input) => {
            //     let aux;
            //     Axios.get('http://localhost:3001/getNames').then((response) => {
            //         console.log(!!response.data.find(e => e === input));
            //         aux = !!response.data.find(e => e === input);
            //     });
            //     return aux;
            // }
            //TODO: checked
            break;
        case 'regArticle':
            label = 'Número de artículo';
            placeholder = 'Ingrese el código del artículo';
            //TODO: checked
            break;
        case 'weight':
            label = 'Peso de la tarea';
            placeholder = 'Ingrese el peso entregado';
            checked = (input) => !isNaN(input) && Number(input) > 0;
            break;
        case 'deadline':
            label = 'Fecha de entrega';
            placeholder = 'Ingrese la fecha de entrega (DD/MM/AAAA)';
            checked = (input) => {
                let error = false;
                let aux = input.split('/');
                aux.map((e) => {
                    if (isNaN(e))
                        error = true;
                })
                return input.length === 10 && input[2] === '/' && input[5] === '/' && !error;
            }
            break;
        case 'observation':
            label = 'Observación/es';
            placeholder = 'Ingrese observaciones sobre la tarea si las tiene';
            checked = (input) => true;
            break;
        case 'calification':
            label = 'Calificación';
            placeholder = 'Ingrese la calificación del 1 al 10';
            checked = (input) => !isNaN(input) && Number(input) > 0 && Number(input) <= 10 && !isNaN(input.split('/'));
            break;
        default:
            console.log("ERROR, HA INGRESADO EL toShow " + toShow + " EN EL COMPONENTE TEXTINPUT");
    }

    label = (auxStorage) ? auxStorage : label;
    placeholder += '…';
    return ((toShow === 'name') ? <NameRequest label={label} placeholder={placeholder} onChange={onChange} /> :
        ((toShow === 'article') ? <ArticleRequest label={label} placeholder={placeholder} onChange={onChange} /> :
            <Form.Group>
                <Form.Label>{label}</Form.Label>
                <InputGroup>
                    {(toShow === 'money' || toShow === 'price') ? <InputGroup.Text>$</InputGroup.Text> : <></>}
                    <FormControl type="text" placeholder={placeholder} defaultValue={defValue} onChange={(e) => {
                        setError(!checked(e.target.value));
                        if (checked(e.target.value))
                            onChange(e);
                    }} />
                    {(toShow === 'weight') ? <InputGroup.Text>kg</InputGroup.Text> : <></>}
                </InputGroup>
                {error ? <em>El valor ingresado no es válido</em> : <></>}
            </Form.Group>)
    );
}

Request.propTypes = {
    onChange: PropTypes.func.isRequired, //Función a ejecutar cuando se modifica el input
    toShow: PropTypes.string.isRequired,
    label: PropTypes.string
}

const ArticleRequest = ({ label, placeholder, onChange }) => {

    const [id, setId] = useState('');
    const [description, setDescription] = useState('');
    const [error, setError] = useState(false);
    const [show, setShow] = useState(false);

    const getDescription = (userInput) => {
        Axios.post('http://localhost:3001/getDescriptionWhere', { id: userInput }).then((response) => {
            setError(response.data.length === 0);
            if (response.data.length !== 0)
                setDescription(response.data[0].description);
            else
                setDescription('');
        });
    }

    const popover = (
        <Popover>
            <Popover.Header as="h3">Descripción de artículo</Popover.Header>
            <Popover.Body>
                {error ?
                    <em>
                        <p>No existe este código de artículo</p>
                        <Button onClick={() => {
                            setId('');
                            setShow(true);
                        }}>Ingresar</Button>
                    </em>
                    : description}
            </Popover.Body>
        </Popover >
    );


    if (!show)
        return (
            <Form.Group className="mb-3">
                <Form.Label>{label}</Form.Label>
                <InputGroup>
                    <OverlayTrigger show={id !== ''} placement="right" overlay={popover}>
                        <FormControl type="text" placeholder={placeholder} onChange={(e) => {
                            onChange(e);
                            setId(e.target.value)
                            getDescription(e.target.value);
                        }} />
                    </OverlayTrigger>
                </InputGroup>
            </Form.Group>);
    return (<ModalArt handleClose={() => setShow(false)} show={show} registration={true} />);
}

ArticleRequest.propTypes = {
    label: PropTypes.string.isRequired,
    placeholder: PropTypes.string.isRequired,
    onChange: PropTypes.func.isRequired
}

const NameRequest = ({ label, placeholder, onChange }) => {

    const [list, setList] = useState([]);

    useEffect(() => {
        getList();
    }, []);
    const getList = () => {
        Axios.get('http://localhost:3001/getNames').then((response) => {
            setList(response.data);
        });
    }
    return (
        <Form.Group className="mb-3">
            <Form.Label>{label}</Form.Label>
            <Autocomplete suggestions={list.map((element) => { return element.name })}
                onChange={onChange} placeholder={placeholder} updateList={getList} />
        </Form.Group>
    );
}

NameRequest.propTypes = ArticleRequest.propTypes;

class Autocomplete extends Component {
    static propTypes = {
        suggestions: PropTypes.instanceOf(Array).isRequired,
        onChange: PropTypes.func.isRequired,
        placeholder: PropTypes.string,
        updateList: PropTypes.func.isRequired
    };

    static defaultProps = {
        placeholder: 'Ingrese el nombre del/la tallerista'
    };

    constructor(props) {
        super(props);

        this.state = {
            // The active selection's index
            activeSuggestion: 0,
            // The suggestions that match the user's input
            filteredSuggestions: [],
            // Whether or not the suggestion list is shown
            showSuggestions: false,
            // What the user has entered
            userInput: "",
            showModal: false
        };
    }

    onChange = e => {
        const { suggestions } = this.props;
        const userInput = e.currentTarget.value;

        // Filter our suggestions that don't contain the user's input
        const filteredSuggestions = suggestions.filter(
            suggestion =>
                suggestion.toLowerCase().indexOf(userInput.toLowerCase()) > -1
        );

        this.setState({
            activeSuggestion: 0,
            filteredSuggestions,
            showSuggestions: true,
            userInput: e.currentTarget.value
        });
        this.props.onChange(e.currentTarget.value);
    };

    onClick = e => {
        this.setState({
            activeSuggestion: 0,
            filteredSuggestions: [],
            showSuggestions: false,
            userInput: e.currentTarget.innerText
        });
        this.props.onChange(e.currentTarget.innerText);
    };

    render() {
        const {
            onChange,
            onClick,
            onKeyDown,
            state: {
                activeSuggestion,
                filteredSuggestions,
                showSuggestions,
                userInput
            }
        } = this;

        let suggestionsListComponent;

        if (showSuggestions && userInput) {
            if (filteredSuggestions.length) {
                suggestionsListComponent = (
                    <ul className="suggestions">
                        {filteredSuggestions.map((suggestion, index) => {
                            let className;

                            // Flag the active suggestion with a class
                            if (index === activeSuggestion) {
                                className = "suggestion-active";
                            }

                            return (
                                <li className={className} key={suggestion} onClick={onClick}>
                                    {suggestion}
                                </li>
                            );
                        })}
                    </ul>
                );
            } else {
                suggestionsListComponent = (
                    <div className="no-suggestions">
                        <em>El taller ingresado no existe </em>
                        <Button onClick={() => {
                            this.setState({
                                showModal: true
                            })

                        }}>Ingresar</Button>
                    </div>
                );
            }
        }

        if (!this.state.showModal)
            return (
                <Fragment>
                    <FormControl
                        placeholder={this.props.placeholder}
                        type="text"
                        onChange={onChange}
                        onKeyDown={onKeyDown}
                        value={userInput}
                    />
                    {suggestionsListComponent}
                </Fragment>
            );
        else
            return (<ModalWork handleClose={() => {
                this.setState({ showModal: false });
                this.props.updateList();
            }}
                show={this.state.showModal} registration={true} />);
    }
}
