import React, { Component } from 'react';

export class ModalController extends Component {

    state = {
        showModal: false
    }

    showModal = () => {
        this.setState({ showModal: true });
    }

    hideModal = () => {
        this.setState({ showModal: false });
    }


}

export class ModalPrototype extends Component {
    render() {
        return (
            <div style={{ 'display': this.props.show ? 'block' : 'none', 'background': 'yellow' }}>
                <section>
                    <button type="button" onClick={this.props.handleClose}>
                        Close
                    </button>
                </section>
                {this.props.children}
            </div>
        );
    }
}
