import React, { useState, useEffect, Component, Fragment } from 'react';
import PropTypes from 'prop-types';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import Popover from 'react-bootstrap/Popover';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger'
import Axios from 'axios';
import Button from 'react-bootstrap/Button';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import { ModalArt, ModalWork } from './Menu/RegistrationDrop/modals'
import './autocomplete.css';

export const Request = ({ onChange, toShow, label }) => {//Pedirá un dato con un input tipo text

    const [error, setError] = useState(false);

    let placeholder, auxStorage = label, checked = (input) => input !== '';


    switch (toShow) {
        case 'name':
            label = 'Nombre del taller';
            placeholder = 'Ingrese el nombre del/la tallerista';
            break;
        case 'article':
            label = 'Número de artículo';
            placeholder = 'Ingrese el código único del artículo';
            break;
        case 'quantityArticle':
            label = 'Cantidad asignada';
            placeholder = 'Ingrese la cantidad de artículos';
            checked = (input) => !isNaN(input) && Number(input) > 0 && Number(input) === Math.round(Number(input));
            break;
        case 'money':
            label = 'Monto';
            placeholder = 'Ingrese el monto a pagar';
            checked = (input) => !isNaN(input) && Number(input) > 0;
            break;
        case 'price':
            label = 'Precio por unidad';
            placeholder = 'Ingrese el precio por unidad';
            checked = (input) => !isNaN(input) && Number(input) > 0;
            break;
        case 'description':
            label = 'Descripción';
            placeholder = 'Ingrese una breve descripción del artículo';
            break;
        case 'regName':
            label = 'Nombre del taller';
            placeholder = 'Ingrese el nombre del/la tallerista';
            checked = async (input) => {
                if (input === '')
                    return false
                let response = await Axios.get('http://localhost:3001/getNames');
                response = !response.data.find(e => e.name.toLowerCase() === input.toLowerCase());
                return response;
            };
            break;
        case 'regArticle':
            label = 'Número de artículo';
            placeholder = 'Ingrese el código del artículo';
            checked = async (input) => {
                if (input === '' || isNaN(input))
                    return false;
                let response = await Axios.post('http://localhost:3001/getDescriptionWhere', { id: input });
                return response.data.length === 0;
            };
            break;
        case 'weight':
            label = 'Peso de la tarea';
            placeholder = 'Ingrese el peso entregado';
            checked = (input) => !isNaN(input) && Number(input) > 0;
            break;
        case 'deadline':
            label = 'Fecha esperada';
            placeholder = 'Ingrese la fecha esperada (DD/MM/AAAA)';
            checked = (input) => {
                let error = false;
                let aux = input.split('/');
                aux.map((e) => {
                    if (isNaN(e))
                        error = true;
                })
                return input.length === 10 && input[2] === '/' && input[5] === '/' && !error;
            }
            break;
        case 'observation':
            label = 'Observación/es';
            placeholder = 'Ingrese observaciones sobre la tarea si las tiene';
            checked = (input) => true;
            break;
        case 'calification':
            label = 'Calificación';
            placeholder = 'Ingrese la calificación del 1 al 10';
            checked = (input) => !isNaN(input) && Number(input) > 0 && Number(input) <= 10 && Number(input) === Math.round(Number(input));
            break;
        case 'packages':
            label = 'Bultos';
            placeholder = 'Ingrese la cantidad de bultos';
            checked = (input) => !isNaN(input) && Number(input) > 0 && Number(input) === Math.round(Number(input));
            break;
        case 'fabrics':
            label = 'Telas';
            placeholder = 'Ingrese las telas';
            checked = (input) => true;
            break;
        case 'responsable':
            label = 'Responsables';
            placeholder = 'Ingrese las personas responsables';
            break;
        case 'generalFeatures':
            label = 'Características generales';
            placeholder = 'Ingrese las características generales';
            break;
        case 'colors':
            label = 'Colores';
            placeholder = 'Ingrese los colores';
            break;
        case 'threads':
            label = 'Hilos';
            placeholder = 'Ingrese la cantidad de hilos';
            break;
        default:
            console.error("ERROR, HA INGRESADO EL toShow " + toShow + " EN EL COMPONENTE TEXTINPUT");
    }

    label = (auxStorage) ? auxStorage : label;
    placeholder += '…';
    return ((toShow === 'name') ? <NameRequest label={label} placeholder={placeholder} onChange={onChange} /> :
        ((toShow === 'article') ? <ArticleRequest label={label} placeholder={placeholder} onChange={onChange} /> :
            <Form.Group>
                <Form.Label>{label}</Form.Label>
                <InputGroup>
                    {(toShow === 'money' || toShow === 'price') ? <InputGroup.Text>$</InputGroup.Text> : <></>}
                    <FormControl type="text" placeholder={placeholder} onChange={(e) => {
                        let res;
                        if (checked[Symbol.toStringTag] !== 'AsyncFunction') {
                            res = checked(e.target.value);
                            setError(!res);
                            if (res)
                                onChange(e);
                        }
                        else
                            checked(e.target.value).then(result => {
                                setError(!result);
                                if (result)
                                    onChange(e);
                            });
                    }} />
                    {(toShow === 'weight') ? <InputGroup.Text>kg</InputGroup.Text> : <></>}
                </InputGroup>
                {error ? <em>El valor ingresado no es válido</em> : <></>}
            </Form.Group>)
    );
}
Request.propTypes = {
    onChange: PropTypes.func.isRequired, //Función a ejecutar cuando se modifica el input
    toShow: PropTypes.string.isRequired,
    label: PropTypes.string
}

const ArticleRequest = ({ label, placeholder, onChange }) => {
    const [articles, setArticles] = useState([]);

    const getList = () => {
        Axios.get('http://localhost:3001/getArticles').then((response) => {
            setArticles(response.data.map(article => article.id + ': ' + article.description));
        })
    }
    getList();

    return(
        <Autocomplete suggestions={articles} onChange={onChange}
            placeholder="Seleccione un artículo..." updateList={getList} />
    );
}
ArticleRequest.propTypes = {
    label: PropTypes.string.isRequired,
    placeholder: PropTypes.string.isRequired,
    onChange: PropTypes.func.isRequired
}

const NameRequest = ({ label, placeholder, onChange }) => {

    const [list, setList] = useState([]);

    useEffect(() => {
        getList();
    }, []);
    const getList = () => {
        Axios.get('http://localhost:3001/getNames').then((response) => {
            setList(response.data);
        });
    }
    return (
        <Form.Group className="mb-3">
