import React from 'react';
import { TextInput } from '../../textInput';
import PropTypes from 'prop-types';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button'

export const Workshop = ({ registration }) => {//Toma un valor booleano
    var name = '', contact = '';
    if (registration)//de ser true, se está dando el alta, de lo contrario, la baja
        return (
            <Form>
                <Form.Group className="mb-3">
                    <Form.Label>Nombre del taller</Form.Label>
                    <Form.Control type="text" placeholder="Ingrese el nombre del/la tallerista…" onChange={(event) => {
                        workshop = event.target.value;
                    }} />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Label>Datos de contacto</Form.Label>
                    <Form.Control type="textarea" placeholder="Ingrese datos de contacto del tallerista…" onChange={(event) => {
                        contact = event.target.value;
                    }} />
                </Form.Group>
            </Form>
        );
    else
        return (
            <Form>
                <Form.Group className="mb-3">
                    <Form.Label>Nombre del taller</Form.Label>
                    <Form.Control type="text" placeholder="Ingrese el nombre del/la tallerista…" onChange={(event) => {
                        workshop = event.target.value;
                    }} />
                </Form.Group>
            </Form>
        );
}

Workshop.propTypes = {
    registration: PropTypes.bool.isRequired //True si y sólo si se da un alta
}
