// myContainer.js
import React, { Component } from 'react'

export default class MyContainer extends Component {
  state = {
    show: false
  }

  handleNameChange = reg => {
    this.setState({ show: reg })
    console.debug("HI");
  }

  render() {
    return (
      <MyChild onNameChange={this.handleNameChange} />
    )
  }

}
// myChild.js
class MyChild extends Component {

  handleInputChange = event => {
    this.props.onNameChange(event.target.value)
  }

  render() {
    return (
      <div>
        <button onClick={this.handleInputChange} value={true}>Hello</button>
      </div>
    )
  }

}

