import React, { Component } from 'react';
import { ModalOpener } from './modalOpener';
import { Request, TaskRequest } from '../textInputs';
import Form from 'react-bootstrap/Form';
import image from './Images/DeliverTask.svg';
import moment from 'moment';
import Axios from 'axios';

export class DeliverTask extends Component {
    state = {
        task: '',
        quantity: '',
        money: '',
        weight: '',
        spare: false
    }

    changeValue = (state, val) => {
        this.setState({ [state]: val });
    }

    myForm = () => {
        return (
            <Form>
                <Request toShow="name" onChange={(element) => {
                    this.setState({ name: element });
                }} />

                <TaskRequest name={this.state.name} onChange={(data, task) => {
                    this.setState({ task, quantity: data.quantity, money: data.money, weight: data.weight });
                }} />

                <Form.Check
                    type={'checkbox'}
                    label="Perdonar lo faltante"
                    onChange={() => this.setState({ spare: !this.state.spare })}
                />
            </Form>
        );
    }

    post = () => {
        Axios.put('http://localhost:3001/newPart',
            {
                task: this.state.task, date: moment(new Date()).format("DD/MM/YYYY"), quantity: this.state.quantity,
                weight: this.state.weight, money: this.state.money
            });
    }

    render() {
        return (
            <ModalOpener buttonText='Recibir tarea' children={this.myForm()}
                className={'title'} logo={image} title={'Ingreso de tarea'} post={this.post} />
            //Crea un bot칩n que abre a un modal en el que aparecer치 lo devuelto en this.myForm
        );
    }
}
