import React from 'react';
import { TextInput } from '../../textInput';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button'
import PropTypes from 'prop-types';
import Axios from 'axios';

export const Article = ({ registration }) => {

    var id = 0, price = 0, description = 0;

    if (registration) {//de ser true, se está dando el alta, de lo contrario, la baja
        
        return (
            <Form>
                <Form.Group className="mb-3">
                    <Form.Label>Número de Artículo</Form.Label>
                    <Form.Control type="text" placeholder="Ingrese el número único…" onChange={(event) => {
                        id = event.target.value;
                    }}/>
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Label>Precio por unidad</Form.Label>
                    <Form.Control type="text" placeholder="Ingrese el precio…"  onChange={(event) => {
                        price = event.target.value;
                    }}/>
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Label>Descripción</Form.Label>
                    <Form.Control type="text" placeholder="Ingrese una descripción del artículo…"  onChange={(event) => {
                        description = event.target.value;
                    }}/>
                </Form.Group>
            </Form>
        );
    }
    else
        return (
            <form>{/*Devuelve el form para dar de baja a un artículo. Pide todos los datos necesarios*/}
                <TextInput name={'Number'} label={'Número de Artículo'} />
                <button onClick={function () {
                    Axios.post('http://localhost:3001/create', {id, price, description});
                }}>Aceptar</button>
            </form>
        );
}

Article.propTypes = {
    registration: PropTypes.bool.isRequired //True si y sólo si se da un alta
}
