import React, { Component } from 'react';
import { ModalOpener } from './modalOpener';
import { TextInput } from '../textInput';
import image from './Images/NewPayment.svg';
import Axios from 'axios';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button'

export class NewPaymentButton extends Component {
    myForm = () => {
        var workshop = '', quantity = '';
        return (
            <Form>
                <Form.Group className="mb-3">
                    <Form.Label>Nombre del taller</Form.Label>
                    <Form.Control type="text" placeholder="Ingrese el nombre del/la tallerista…" onChange={(event) => {
                        workshop = event.target.value;
                    }} />
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Label>Cantidad entregada</Form.Label>
                    <Form.Control type="text" placeholder="Ingrese la cantidad de artículos…" onChange={(event) => {
                        quantity = event.target.value;
                    }} />
                </Form.Group>
            </Form>
        );
    }

    render() {
        return (
            <ModalOpener buttonText='Ingresar pago' children={this.myForm()}
                className={'title'} logo={image} title={'Ingreso de pago'} post={() => {
                    Axios.post('http://localhost:3001/create', {});
                }} />
            //Crea un botón que abre a un modal en el que aparecerá lo devuelto en this.myForm
        );
    }
}
