import React, { Component } from 'react';
import { ModalOpener } from './modalOpener';
import { Request } from '../textInputs';
import image from './Images/NewTask.svg';
import Axios from 'axios';
import Form from 'react-bootstrap/Form';
import moment from 'moment';

const initialState = {
    task: '',
    error: true,
    errors: [true, true, true, true, true, true, true],
    formRef: React.createRef(),
    article: '',
    quantity: '',
    packages: '',
    cutDate: '',
    fabrics: '',
    colors: '',
    responsable: '',
    generalFeatures: '',
}

export class NewTaskButton extends Component {
    state = initialState;

    resetState = () => {
        let task = this.state.task;
        this.setState(initialState);
        this.setState({ task });
    }

    updateError = (index, error) => {
        let errors = [...this.state.errors];
        errors[index] = error;
        this.setState({ errors });

        let aux = false;
        errors.map((e, key) => {
            aux = aux && (key === index) ? error : e;
        });
        this.setState({ error: aux });
    }

    handleEnter = (event) => {
        if (event.key.toLowerCase() === "enter") {
          const form = event.target.form;
          const index = [...form].indexOf(event.target);
          form.elements[index + 1].focus();
          console.log(form.elements[index + 1]);
          event.preventDefault();
        }
      };

    myForm = () => {
        Axios.get('http://localhost:3001/getTaskCount').then(response => {
            let res = response.data[0].count;
            let limit = 9;//TODO: Cambiar a 999
            res = (res + 1 > limit) ? 1 : res + 1;//Límite cantidad de tareas
            this.setState({ task: res });
        });
        return (
            <Form>
                <h3>Tarea número {this.state.task}</h3>
                <Request toShow="article" onChange={(event, error) => {
                    this.setState({ article: event });
                    this.updateError(0, error);
                }} />

                <Request toShow="quantityArticle" onChange={(event, error) => {
                    this.setState({ quantity: event.target.value });
                    this.updateError(1, error);
                }} />

                <Request toShow="packages" onChange={(event, error) => {
                    this.setState({ packages: event.target.value });
                    this.updateError(2, error);
                }} />

                <Request toShow="fabrics" onChange={(event, error) => {
                    this.setState({ fabrics: event.target.value });
                    this.updateError(3, error);
                }} />

                <Request toShow="colors" onChange={(event, error) => {
                    this.setState({ colors: event.target.value });
                    this.updateError(4, error);
                }} />

                <Request toShow="responsable" onChange={(event, error) => {
                    this.setState({ responsable: event.target.value });
                    this.updateError(5, error);
                }} />

                <Request toShow="generalFeatures" onChange={(event, error) => {
                    this.setState({ generalFeatures: event.target.value });
                    this.updateError(6, error);
                }} />
            </Form>
        );
    }

    post = () => {
        let aux = this.state;
        this.resetState();
        Axios.post('http://localhost:3001/getDescriptionWhere', { id: aux.article }).then((response) => {
            let description = response.data[0].description;
            Axios.delete(`http://localhost:3001/deleteTask/${aux.task}`).then(() => {
                Axios.post('http://localhost:3001/updateTaskCount', { task: aux.task });
                Axios.post('http://localhost:3001/newTask',
                    {
                        id: aux.task, article: aux.article, description, quantity: aux.quantity, packages: aux.packages,
                        cutDate: moment(new Date()).format("DD/MM/YYYY"), fabrics: aux.fabrics, colors: aux.colors,
                        responsable: aux.responsable, generalFeatures: aux.generalFeatures
                    });
            });
        });
    }
    render() {
        return (
            <ModalOpener buttonText='Nueva tarea' children={this.myForm()} error={this.state.error} className={'title'}
                logo={image} title={'Creación de tarea'} post={this.post} handleClose={this.resetState} />
            //Crea un botón que abre a un modal en el que aparecerá lo devuelto en this.myForm
        );
    }
}
